css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linuxquestions.org") {
  img[src="https://lqo-thequestionsnetw.netdna-ssl.com/questions/images/LinuxQuestions.png"] { display: none !important; }
  
  html, body, .page { background-color: #000C17 !important; color: #777 !important; }
  .tborder { background-color: transparent !important; border: 1px solid #00ACAF !important; }
  .vbmenu_control, .tcat { background: #000C27 none no-repeat scroll left top !important; }
  .alt1, .alt1Active { background-color: transparent !important; color: #777 !important; }
  .thead { background: transparent none no-repeat scroll left top !important; }
  .tcat { color: #00ACAF !important; }
  .alt2, .alt2Active { background-color: #000C27 !important; color: #00ACAF !important; } /* */
  
  .tfoot { background-color: #000C27 !important; }
  
  font[color="black"] { color: #777 !important; }
  
  tbody[id*="collapseobj_module_"]:hover { background-color: #000C47 !important; } /* main page highlight */
  tbody > tr:hover { background-color: #000C47 !important; } /* other tables highlight */
  
  /* posts row highlight */
  div#posts table[id*="post"] .alt2 { background-color: transparent !important; }
  div#posts table[id*="post"]:hover { background-color: #000C47 !important; }
  
  /* popups */
  .vbmenu_popup { background-color: #000C17 !important; border: 1px solid #00ACAF !important; }
  .vbmenu_option { background-color: #000C27 !important; color: #00ACAF !important; }
  .vbmenu_hilite { background-color: #000C47 !important; }
  
  /* post editor */
  .panelsurround, .panel { background-color: #000C27 !important; color: #00ACAF !important; }
  .vBulletin_editor { background-color: #000C27 !important; }
  .imagebutton { background-color: transparent !important; border: 1px solid #000CC7 !important; }
  .imagebutton:hover { border: 1px solid #00ACAF !important; }
  .alt_pickbutton { border-left-color: #00ACAF !important; }
  
  /* post editor images */
  img[alt="Remove Text Formatting"] { height: 0 !important; width: 0 !important; padding-left: 21px !important; padding-top: 20px !important; background: url("http://i.imgur.com/NjpZLoD.gif") no-repeat !important; background-size: contain !important; }
  img[alt="Bold"] { height: 0 !important; width: 0 !important; padding-left: 21px !important; padding-top: 20px !important; background: url("http://i.imgur.com/A3pBIz7.gif") no-repeat !important; background-size: contain !important; }
  img[alt="Italic"] { height: 0 !important; width: 0 !important; padding-left: 21px !important; padding-top: 20px !important; background: url("http://i.imgur.com/sgRJXgd.png") no-repeat !important; background-size: contain !important; }
  img[alt="Underline"] { height: 0 !important; width: 0 !important; padding-left: 21px !important; padding-top: 20px !important; background: url("http://i.imgur.com/HH6VknU.png") no-repeat !important; background-size: contain !important; }
  img[id="vB_Editor_QR_color_bar"] { height: 0 !important; width: 0 !important; padding-left: 21px !important; padding-top: 4px !important; background: red none no-repeat !important; background-size: contain !important; }
  img[src="https://lqo-thequestionsnetw.netdna-ssl.com/questions/images/editor/color.gif"] { height: 0 !important; width: 0 !important; padding-left: 21px !important; padding-top: 20px !important; background: url("http://i.imgur.com/nJN1fjo.png") no-repeat !important; background-size: contain !important; }
  img[src="https://lqo-thequestionsnetw.netdna-ssl.com/questions/images/editor/menupop.gif"] { height: 0 !important; width: 0 !important; padding-left: 11px !important; padding-top: 16px !important; background: url("http://i.imgur.com/3bYPR1V.gif") no-repeat !important; background-size: contain !important; }
  img[src="https://lqo-thequestionsnetw.netdna-ssl.com/questions/images/editor/separator.gif"] { height: 0 !important; width: 0 !important; padding-left: 1px !important; padding-top: 21px !important; background: #00ACAF none no-repeat !important; background-size: contain !important; }
  
  /* other images */
  img[src="https://lqo-thequestionsnetw.netdna-ssl.com/questions/images/statusicon/forum_old.gif"] { height: 0 !important; width: 0 !important; padding-left: 24px !important; padding-top: 28px !important; background: url("http://i.imgur.com/CiTpShV.png") no-repeat !important; background-size: contain !important; }
  img[src="https://lqo-thequestionsnetw.netdna-ssl.com/questions/images/statusicon/forum_new.gif"] { height: 0 !important; width: 0 !important; padding-left: 24px !important; padding-top: 28px !important; background: url("http://i.imgur.com/fFeaBUn.png ") no-repeat !important; background-size: contain !important; }
  img[src="https://lqo-thequestionsnetw.netdna-ssl.com/questions/images/statusicon/../icons/useragent/icon_linux.gif"] { height: 0 !important; width: 0 !important; padding-left: 20px !important; padding-top: 20px !important; background: url("http://i.imgur.com/efVEFmY.png") no-repeat !important; background-size: contain !important; }
  
  /* post borders */
  table[id*="post"] td.alt2, table[id*="post"] td.alt1, table[id*="post"] td.thead { border-color: #00ACAF !important; }
  hr { color: #00ACAF !important; }
  
  /* .alt1Active:hover { background-color: #000C47 !important; } /* */
  
  .bbcodeblock { background-color: #000C17 !important; border: 1px dashed #00ACAF !important; }
  input, select, textarea { background-color: #000C17 !important; color: #00ACAF !important; border: 1px solid #00ACAF !important; }
  .fjdpth0, .fjsel { background-color: #000C47 !important; color: #00ACAF !important; }
  a:link, a:visited { color: #00ACAF !important; }
}
