css:
@-moz-document url-prefix("https://www.joduska.me/forum/") {

#secondary_navigation {
    background: #D91E18;
    border: 1px solid #96281B;
}
ol.breadcrumb li.first:hover {
    background: #96281B;
}
.input_submit:hover {
    background: #96281B;
    border-color: #96281B;
}
.post_block.feature_box {
    background: #F3E3E6;
    border: 1px solid #E599AA;
}

/*#shoutbox-shouts-table tr.row2:nth-child(2n+1) {
    background: #FFF0F0;
}*/

div#shoutbox-announcement-text {
    background: #E6E6E6;
    border-color: #D6D6D6;
}
.post_block.feature_box .ipsBadge_green,
.post_block.feature_box .ipsBadge_lightgrey {
    background-color: rgb(255, 100, 100);
}
.input_submit.alt:hover {
    background: #96281B;
    border-color: #96281B;
}
.pagination .pages li.active {
    border: 1px solid #96281B;
}
.pagination li.page:hover {
    background: #D91E18;
}
tr.row1 td.altrow input.shoutbox-submit-button:hover {
    background: #D91E18;
}
.maintitle,
.col_c_icon img[src*='f_'],
.f_icon,
.topic_buttons li a,
.pagination .pages li.active,
.post_block h3,
.mini_pagination a,
#vnc_filter_popup_close,
.col_f_icon img,
.ipsBadge_green,
.input_submit.alt,
.popupInner h3 {
    background-color: #D91E18;
}
.ipbfs_titlebox:before {
    background: #D91E18;
    border: 1px solid #96281B;
}
.maintitle {
    color: #FFF;
    border: 1px solid #96281B;
}
.input_submit.alt {
    border: 1px solid #96281B;
}
#profile_panes_wrap .ipsFilterbar {
    background: #D91E18;
    border: 1px solid #96281B;
}
.input_submit {
    background: #D91E18;
    border: 1px solid #96281B;
}
#profile_panes_wrap.post_block:not {
    border-bottom: 3px solid #D91E18;
}
#secondary_navigation #breadcrumb li {
    background: #96281B;
    border: 0 #96281B;
}
.post_block.feature_box .ipsType_sectiontitle {
    border-color: #96281B;
}
.poll .submit {
    border-bottom: 6px solid #D91E18;
}
.poll h3 {
    background: #D91E18;
    border: 1px solid #96281B;
}
#tabs li a.active {
    background: #D91E18!important;
    border: 1px solid #96281B!important;
    text-shadow: 0 1px 1px #96281B!important;
    -webkit-box-shadow: inset 0 1px 0 #96281B!important;
    -moz-box-shadow: inset 0 1px 0 #96281B!important;
    box-shadow: inset 0 1px 0 #96281B!important;
}
#tabs li a.active {
    background: #D91E18!important;
    border: 1px solid #96281B!important;
    text-shadow: 0 1px 1px #96281B!important;
    -webkit-box-shadow: inset 0 1px 0 #96281B!important;
    -moz-box-shadow: inset 0 1px 0 #96281B!important;
    box-shadow: inset 0 1px 0 #96281B!important;
}
.dotabtn.leaguebtn {
    background: #D91E18;
    border: 1px solid #96281B;
}
.cbox.gray,
#topicwrap .gray.cbox2 {
    background: #F3E3E6;
    border: 1px solid #E599AA;
}
#topicwrap .summary button.btn {
    border: 1px solid #96281B;
    background: #D91E18;
}
#topicwrap .content h1,
#topicwrap .summary h1,
#topicwrap .content h2,
#topicwrap .summary h2,
#topicwrap .content h3,
#topicwrap .summary h3 {
    color: #D91E18;
}
#topicwrap .content blockquote,
#topicwrap .summary blockquote {
    border-left: 3px solid #D91E18;
}
#topicwrap .summary .open-ticket-holder .open-ticket-close {
    background: #D91E18;
    border-left: 1px solid #96281B;
    border-top: 1px solid #96281B;
}
#messenger_utilities.left .ipsSideBlock h3 {
    background: #D91E18;
    border: 1px solid #96281B;
}
#topicwrap footer span.red {
    color: #D91E18;
}
#themeEditor #editPattern span,
ul.post_controls a:hover,
.answerBadgeInPost,
.ipsLikeButton.ipsLikeButton_enabled,
.ipsTag {
    background-color: #D91E18;
}
.topic_buttons li.important a,
.topic_buttons li.important span,
.ipsButton .important,
.topic_buttons li a,
.topic_buttons li span,
.ipsButton {
    background: #D91E18;
    border: 1px solid #96281B;
}
#socialLinks li {
    background: #D91E18;
}
.post_block {
    border-bottom: 6px solid #D91E18;
}
a:hover {
    color: #d35400;
}
body {
    background: #000000;
}
#secondary_links>li a {
    background: #D91E18;
}
.answerBadgeInPost {
    border: 1px solid #96281B;
}
.ggbg {
    background: url('https://i.imgur.com/RZ25JtV.png') no-repeat 50% 0;
}
.message {
    background: #F3E3E6;
    border: 1px solid #E599AA;
}
.blur_ubar {
    background: url('https://i.imgur.com/t2SMDyQ.png') no-repeat 50% 0;
    -webkit-filter: blur(5px);
    -moz-filter: blur(5px);
    -o-filter: blur(5px);
    -ms-filter: blur(5px);
    filter: blur(5px);
}
.blur_menu {
    background: url('https://i.imgur.com/0DwlyVm.png') no-repeat 50% 0;
    -webkit-filter: blur(5px);
    -moz-filter: blur(5px);
    -o-filter: blur(5px);
    -ms-filter: blur(5px);
    filter: blur(5px);
}
ul.post_controls li:hover {
    border: 1px solid #96281B;
}
ul.ipsList_inline.left.pages li:hover {
    background: #D91E18;
    border: 1px solid #96281B;
}
#primary_nav {
    background: rgba(231, 76, 60, 0.4);
}
#user_bar {
    background: rgba(231, 76, 60, 0.4);
}
}
