@-moz-document url-prefix("http://www.youtube.com/watch"), url-prefix("https://www.youtube.com/watch") {
    #content,
    #player,
    body {
        background: #000000 !important;
        transition: background 0s ease-in-out !important;
    }
    body:hover #content,
    body:hover #player,
    body:hover {
        background: #000000 !important;
    }

    #masthead-appbar-container,
    #watch7-sidebar,
    #watch7-content {
        opacity: 0 !important;
        transition: opacity 0s ease-in-out !important;
    }
    body:hover #masthead-appbar-container,
    body:hover #watch7-sidebar,
    body:hover #watch7-content {
        opacity: 1 !important;
    }

    #theater-background,
    #yt-masthead-container {
        opacity: 0 !important;
        transition: opacity 0s ease-in-out !important;
    }
    body:hover #theater-background,
    body:hover #yt-masthead-container {
        opacity: 1 !important;
    }

    #footer-container,
    #guide {
        opacity: 0 !important;
        transition: opacity 0s ease-in-out !important;
    }
    body:hover #footer-container,
    body:hover #guide {
        opacity: 1 !important;
    }

    .player-width {
        box-shadow: 0px 0px 0px 0px #000000 !important;
        transition: box-shadow 0s ease-in-out !important;
    }
    body:hover .player-width {
        box-shadow: 0px 0px 0px 0px #000000 !important;
    }

}
