css:
@-moz-document domain("diasp.eu" ) {

html,body, #login {
background: #FFFFCC url(http://frankie-go.de/png/Gaensebluemchen1.png) no-repeat fixed left bottom;
background-size: 25%;
}

a:link {
color: #40A040;
}

#banner, #steps, #links {
background-color: rgba(255,255,255,0.4) !important;
border-radius: 3px !important;
}

#huge-text {
color: #40A040 !important;
}

span a:link, #user_menu a:link, #btn-default {
color: #939393;
}

.disabled, .btn, .button, #login-link {
color: black !important;
}

.btn-primary {
color: white !important;
}

[id='_dropdown'] {
background-color: #D0F0D0 !important;
}

[id='dropdown'] a:link, .hoverable a:link, .all_aspects a:link, a[data-stream='followedtags'] {
color: #40A040 !important;
}

.conversation_participants {
background-color: #D0F0D0 !important;
}

#aspect_stream_container {
background-color: rgba(255,255,255,0.3);/*#def5de;*/
border-right: 1px #E0E0E0 solid;
border-bottom: 1px #E0E0E0 solid;
border-left: 1px transparent;
border-radius: 3px;
}

.control-group, .control-label, #publisher_textarea_wrapper, #status_message_fake_text, textarea[id*='comment_text_on_' ], input[id*='user'], input[id*='profile'], .as-selections, #profile_bio, select[id*='profile_date_'], select[id*='user'], #message_text, #bitcoin_address, #dogecoin_address {
background-color: #E8F8E8 !important;
border-width: 0px;
}

#stream_container {
background-color: #DEF5DE;
}

#stream_selection, #profile, #aspect_nav, #real-post-content, #conversation_inbox, #faq_nav {
background: rgba(208,240,208,0.8);
border-radius: 1px;
}

}
