@-moz-document regexp("https?://(www.)?wrongplanet.net/(forums/?|your-profile)(.*)") {
  body {
    background: transparent;
    color: #8A8A8A;
    font-family: Arial, sans-serif;
  }
  .interior {
    background: #181818 url('https://i.imgur.com/uR02uJb.png') repeat fixed left top;
  }
  ::-moz-selection {
    color: #FFFFFF;
    background-color: #BF7200;
  }
  ::selection {
    color: #FFFFFF;
    background-color: #BF7200;
  }
  input[type="radio"],
  input[type="checkbox"] {
    -webkit-filter: invert(100%);
            filter: invert(100%);
    margin: 5px 4px 0px 5px;
  }
  .resized-div {
    width: 100% !important;
    max-width: 500px;
  }
  .forumicons {
    width: 25px;
  }
  .forumicons img {
    margin-top: 2px;
  }
  input,
  textarea,
  #your-profile .form-table input,
  #your-profile .form-table textarea,
  #display_facebookurl {
    padding: 5px 10px;
  }
  textarea,
  #your-profile .form-table textarea {
    background-color: #262626;
    border: 1px solid #000000;
    color: #8A8A8A;
  }
  #message {
    border-color: #383838;
  }
  #smileys {
    margin-top: 5px;
  }
  .tablebg td.pm_marked_colour,
  .pm_marked_colour {
    background-color: #A86500;
    border-radius: 7px;
    height: 15px;
    width: 15px;
  }
  .tablebg td.pm_replied_colour,
  .pm_replied_colour {
    background-color: #00426E;
    border-radius: 7px;
    height: 15px;
    width: 15px;
  }
  .tablebg td.pm_friend_colour,
  .pm_friend_colour {
    border-radius: 7px;
    height: 15px;
    width: 15px;
  }
  .tablebg td.pm_foe_colour,
  .pm_foe_colour {
    background-color: #A80000;
    border-radius: 7px;
    height: 15px;
    width: 15px;
  }
  .tablebg td.pm_marked_colour,
  .tablebg td.pm_replied_colour,
  .tablebg td.pm_friend_colour,
  .tablebg td.pm_foe_colour {
    border-radius: 0px !important;
  }
  .tablebg td b.genmed[style="color:red"] {
    color: #FC2B2B !important;
  }
  .row2 input.radio + img {
    margin-left: 2px;
    margin-right: 2px;
  }
  .post {
    background-color: #262626;
    color: #8A8A8A;
    border: 1px solid #000000;
    box-shadow: inset 0px 0px 5px #202020;
  }
  input[type=text] {
    background-color: #3A3A3A !important;
    border: 1px solid #000000 !important;
    color: #898989 !important;
    box-shadow: inset 0px 0px 5px #202020 !important;
  }
  #login .login-username label,
  #login .login-password label {
    display: none !important;
  }
  #login label,
  #header #loginform label {
    display: block;
  }
  #login p.login-remember {
    float: left;
    width: 102px;
    display: inline-block;
    margin-top: 5px;
    margin-bottom: 5px;
  }
  #registerform #wp-submit,
  #loginform #wp-submit {
    border-radius: 15px;
    font-size: 14px !important;
    background: #FF9900;
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#FF9900),color-stop(50%,#FF9900),color-stop(100%,#FF9900));
    background: -webkit-linear-gradient(top,#FF9900 0%,#FF9900 50%,#FF9900 100%);
    background:    -moz-linear-gradient(top,#FF9900 0%,#FF9900 50%,#FF9900 100%);
    background:         linear-gradient(to bottom,#FF9900 0%,#FF9900 50%,#FF9900 100%);
    border: solid 1px #FF9900;
    padding: 4px 8px !important;
    margin-bottom: 2px !important;
  }
  input[type=password],
  #header #login input:not(#wp-submit),
  #registerform input,
  #loginform input:not(#wp-submit),
  #registerform textarea,
  #loginform textarea,
  #register input:not(.form-submit) {
    background-color: #3A3A3A !important;
    border: 1px solid #000000 !important;
    color: #898989 !important;
    box-shadow: inset 0px 0px 5px #202020 !important;
    border-radius: 5px !important;
  }
  .row2 .post {
    border-radius: 0px !important;
  }
  #s {
    background-color: #181818 !important;
    border: none !important;
    color: #898989 !important;
    box-shadow: none !important;
    border-radius: 15px !important;
  }
  input[type=text]:hover,
  input[type=text]:focus,
  input[type=text]:active,
  input[type=password]:hover,
  input[type=password]:focus,
  input[type=password]:active {
    border-color: #000000;
    color: #898989;
  }
  select,
  .btnmain,
  .btnlite,
  #wpua-upload,
  #wpua-remove,
  .button-primary,
  #wpua-file,
  input[type="file"],
  input[type="submit"] {
    color: #B0AEAC;
    background-color: #3A3A3A;
    border: 1px solid #82807C;
    box-shadow: none;
  }
  #wpua-file,
  #wpua-upload,
  #wpua-remove,
  .button-primary {
    cursor: pointer;
  }
  .btnmain:hover,
  .btnlite:hover,
  #wpua-upload:hover,
  #wpua-remove:hover,
  .button-primary:hover,
  input[type="submit"]:hover {
    color: #FF9900;
    background-color: #3A3A3A;
    border-color: #FF9900;
  }
  #abbc3posttable .btnbbcode3,
  #abbc3posttable .option.genmed {
    color: #B0AEAC;
    background-color: #FFFFFF !important;
    border: 1px solid #FFFFFF;
  }
  #abbc3posttable .btnbbcode3:hover,
  #abbc3posttable .option.genmed:hover,
  .editor_Button:hover {
    color: #FF9900;
    background: #FFFFFF !important;
    border-color: #383838;
  }
  /* Work in Progress - Input placeholder style
  #s::-webkit-input-placeholder,
  #s::-moz-placeholder,
  #s::placeholder,
  input[type=text]::-webkit-input-placeholder,
  input[type=text]::-moz-placeholder,
  input[type=text]::placeholder,
  input[type=password]::-webkit-input-placeholder,
  input[type=password]::-moz-placeholder,
  input[type=password]::placeholder {
    color: #808080;
    font-style: italic;
  }
  */
  th,
  .headerrow,
  .footerrow {
    background-color: #0D58A6;
  }
  hr {
    background-color: rgba(0, 0, 0, 0.5);
  }
  #headerright #tabs li {
    font-size: 15px;
  }
