    border-top-left-radius: 0px;
    border-bottom-left-radius: 0px;
  }
  #navigation li a:hover,
  #navigation li .active {
    background: #7E7E7E;
    color: #FFFFFF !important;
  }
  #contentmain h2,
  .interior #contentmain h1,
  .interior #contentleft h1 {
    border-bottom: 1px solid #262626;
    padding-bottom: 5px;
  }
  .informbar {
    padding: 2px 10px;
    background-color: #333333;
    color: #727272;
    margin-bottom: 5px;
    margin-top: 5px;
  }
  .user-col,
  form[action="./memberlist.php?mode=group"] > .tablebg tr:nth-of-type(5) > td:nth-of-type(2) {
    word-break: break-all;
    overflow-wrap: break-word;
    word-wrap: break-word;
    position: relative;
  }
  .user-col img {
    clear: left;
  }
  .avatar img {
    border-radius: 5px;
  }
  .forumtopictitle {
    word-break: keep-all;
    float: none;
  }
  .forumtopictitle h4 {
    color: #BCBCBC;
  }
  .forum-thread .row {
    overflow: hidden;
  }
  .row .action-bar {
    border-top: 1px solid #181818;
  }
  .row1:not([class$="_colour"]),
  .row2 {
    background-color: #232323 !important;
  }
  .row3,
  .cat {
    background-color: #383838 !important;
  }
  #contentright {
    background-color: #262626;
    padding: 10px;
    border-radius: 5px;
  }
  #contentright .block h2 {
    color: #CBCBCB;
  }
  #contentright p {
    color: #8C8C8C;
  }
  #your-profile .form-table th {
    background-color: #232323;
    vertical-align: middle;
    text-align: center;
    border-bottom: 1px solid #121212;
  }
  #your-profile .form-table td {
    background-color: #1B1B1B;
    border-bottom: 1px solid #121212;
  }
  #your-profile .form-table:nth-of-type(2) tr:nth-of-type(3) th,
  #your-profile .form-table:nth-of-type(2) tr:nth-of-type(3) td {
    border-bottom-style: dashed;
  }
  #your-profile .form-table tr:last-child th,
  #your-profile .form-table tr:last-child td {
    border-bottom: none;
  }
  #your-profile label,
  #your-profile label + a {
    vertical-align: baseline;
  }
  #your-profile .form-table th.display_diagnosis_row {
    padding-top: 5px;
    padding-bottom: 5px;
  }
  #tabstage {
    background-color: #4A4A4A;
  }
  #headerright #tabs li a {
    background-color: #333333;
    color: #D2D2D2;
  }
  #headerright #tabs li a.active {
    background-color: #4A4A4A;
    color: #FFFFFF;
  }
  #login p {
    width: 130px;
    margin-bottom: 0px;
  }
  #your-profile .form-table #user_login {
    cursor: not-allowed;
  }
  #your-profile .form-table .indicator-hint{
    float: left;
    margin-bottom: 0px;
    margin-top: 5px;
  }
  #your-profile .form-table #password #pass1 {
    margin-bottom: 10px;
  }
  #your-profile .form-table #password span:nth-of-type(1) {
    width: 282px;
    margin-right: 7px;
    margin-top: -5px;
    float: right;
  }
  #your-profile .form-table #password .short {
    color: #E00000;
  }
  #your-profile .form-table #password .strong {
    color: #00E000;
  }
  #your-profile .form-table .helper {
    visibility: hidden;
    position: relative;
  }
  /* ------------------------ Date of Birth input fix ------------------------ */
  #your-profile .form-table .helper::after {
    content: "mm-dd-yyyy";
    visibility: visible;
    position: absolute;
    top: -3px;
    left: 3px;
  }
  /* ------------------------ ^END ------------------------ */
  .message {
    border-bottom: none;
    margin-bottom: 30px !important;
    padding-top: 10px;
    padding-bottom: 10px;
    background: -webkit-repeating-linear-gradient(45deg, #102E3B, #102E3B 2px, #0C222C 2px, #0C222C 4px) repeat scroll 0% 0%;
    background:    -moz-repeating-linear-gradient(45deg, #102E3B, #102E3B 2px, #0C222C 2px, #0C222C 4px) repeat scroll 0% 0%;
    background:         repeating-linear-gradient(45deg, #102E3B, #102E3B 2px, #0C222C 2px, #0C222C 4px) repeat scroll 0% 0%;
    text-align: center;
    text-shadow: 0px 0px 5px #000000;
    border-radius: 5px;
  }
  #wpua-preview img,
  #wpua-thumbnail img {
    max-height: none;
    border: 5px solid #3C3C3C;
    border-radius: 5px;
    background-color: #181818;
  }
  #primarywrap .inner {
    background-color: rgba(0, 0, 0, 0.25);
    border: none;
  }
  .interior #primarycontent {
    background-color: rgba(0, 0, 0, 0);
    background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(rgba(0, 0, 0, 0)), to(rgba(0, 0, 0, 0)));
    background: -webkit-linear-gradient(top, rgba(0, 0, 0, 0), rgba(0, 0, 0, 0));
    background:    -moz-linear-gradient(top, rgba(0, 0, 0, 0), rgba(0, 0, 0, 0));
    background:         linear-gradient(top, rgba(0, 0, 0, 0), rgba(0, 0, 0, 0));
  }
  .main-forum {
    margin-top: -5px;
  }
  .main-forum .row:nth-of-type(2n),
  .row:nth-of-type(2n) {
    background-color: #252525;
  }
  .main-forum .row:nth-of-type(2n+1),
  .row:nth-of-type(2n+1) {
    background-color: #2E2E2E;
  }
  .viewtopictitle {
    text-transform: none;
  }
  .forumtopicsdisplay,
  .headerrow,
  .footerrow,
  .forumtopicsdisplay tr td {
    border: none;
  }
  .forumtopicsdisplay {
    width: 100%;
  }
  .forumtopicsdisplay + br {
    display: none;
  }
  .main-forum .breadcrumb {
    margin-left: 1px;
  }
  .interior #contentmain h2 {
    margin-bottom: -6px;
    color: #F2F2F2;
  }
  #abbcode_wizards td[colspan="2"] {
    background: #181818;
  }
  .poll-bars {
    -webkit-filter: brightness(21%) grayscale(100%);
            filter: brightness(21%) grayscale(100%);
  }
  .user-col {
    font-family: Arial, sans-serif;
  }
  .message-content img[alt="Image"],
  .avatar img[alt="User avatar"] {
    color: #FF0000;
  }
  .author-signature {
    opacity: 0.5;
    margin-bottom: -12px;
    margin-top: -10px;
  }
  #sig {
    opacity: 0.5;
  }
  .message-content {
    min-height: 213px;
  }
  .message-content .gensmall {
    opacity: 0.5;
  }
  .quotetitle,
  .attachtitle,
  .codetitle {
    color: #787878;
    background-color: #363636;
    border: 0;
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, 0.5);
    font-family: 'Lucida Grande', 'Trebuchet MS', Helvetica, Arial, sans-serif;
  }
  .codetitle {
    padding: 4px;
    font-size: 11px;
    color: #009500;
    background-color: #004100;
  }
  .quotecontent,
  .attachcontent {
    color: #626262;
    background-color: #181818;
    border-bottom: 1px solid #363636;
    border-right: 1px solid #363636;
    border-left: 1px solid #363636;
    border-bottom-right-radius: 5px;
    border-bottom-left-radius: 5px;
  }
  .codecontent {
    color: #006600;
    background-color: #002300;
    border-bottom: 1px solid #004100;
    border-right: 1px solid #004100;
    border-left: 1px solid #004100;
    border-bottom-right-radius: 5px;
    border-bottom-left-radius: 5px;
    text-shadow: 0px 0px 5px #001400;
  }
  .quotetitle,
  .quotecontent {
    box-shadow: 0px 0px 5px rgba(24, 24, 24, 0.3);
  }
  .codetitle,
  .codecontent {
    box-shadow: 0px 0px 5px rgba(0, 35, 0, 0.3);
  }
  /* ------------------------ Spoiler Tag/Quote fix ------------------------ */
  .quotecontent > span[style$="white"],
  .quotecontent > span[style$="#FFF"],
  .quotecontent > span[style$="#FFFFFF"],
  .quotecontent > span[style$="transparent"] {
    color: #626262 !important;
    -webkit-filter: blur(3px);
            filter: blur(3px);
    -webkit-transition: filter 0.5s ease;
       -moz-transition: filter 0.5s ease;
            transition: filter 0.5s ease;
  }
  .quotecontent:hover > span[style$="white"],
  .quotecontent:hover > span[style$="#FFF"],
  .quotecontent:hover > span[style$="#FFFFFF"],
  .quotecontent:hover > span[style$="transparent"] {
    -webkit-filter: blur(0px);
            filter: blur(0px);
  }
  /* ------------------------ ^END ------------------------ */
  .thread-action-bar button,
  .new-topic,
  .thread-action-bar-footer button {
    box-shadow: none;
  }
  .count-col span {
    color: #8A8A8A;
  }
  .postdetails {
    color: #E1E1E1;
  }
  td[style$="lightblue"] {
    background-color: #2C2C2C !important;
  }
  .row .action-bar button,
  .green-action-bar-button {
    border: 1px solid #315574;
    color: #315574;
    border-radius: 5px;
    background: transparent;
    box-shadow: none;
  }
  .row .action-bar button:hover,
  .green-action-bar-button:hover {
    background: #315574;
    color: #FFFFFF;
  }
  .row .action-bar .report {
    border: 1px solid #872525;
    color: #872525;
    background: transparent;
  }
  .row .action-bar .report:hover {
    color: #FFFFFF;
    background: #872525;
  }
  #abbc3posttable tr,
  #abbc3posttable td,
  #abbc3posttable div {
    border: 1px solid #383838;
  }
  #abbc3posttable tr:first-child,
  #abbc3posttable tr:first-child td {
    background: #383838 !important;
  }
  #abbc3posttable tr:last-child {
    background: #181818;
  }
  .posting_topic_review .tablebg > tbody > tr:nth-of-type(2n), 
  .posting_topic_review .tablebg > tbody > tr:nth-of-type(2n+1){
    background-color: #181818;
  }
  .posting_topic_review .tablebg > tbody > tr:last-of-type {
    display: none;
  }
  .username-coloured {
    font-weight: normal;
  }
  .posting_topic_review .postauthor {
    color: #A8A8A8;
  }
  a.username-coloured,
  a.username-coloured:link,
  a[href*="./memberlist.php?mode=viewprofile&u="],
  b[style="color: #AA0000"] {
    color: #5387BD !important;
  }
  a {
    outline: 0;
  }
  a,
  a:link {
    color: #5387BD;
  }
  a:hover,
  a:link:hover,
  a.username-coloured:hover {
    color: #FF9900 !important;
  }
  a:visited {
    color: #718AA4;
  }
  a:active:hover {
    text-decoration: underline;
  }
  #footer {
    padding: 5px 0 0 0;
  }
  #footer .inner {
    width: 100% !important;
  }
  #footer #lastnav {
    border-radius: 0;
    background-color: #242424;
    border-color: #242424;
    color: #A8A8A8;
  }
  th[nowrap="nowrap"] {
    text-align: center;
  }
