  td[bgcolor="#000000"]{background-color:#000000;}td[bgcolor="#000040"]{background-color:#000040;}td[bgcolor="#000080"]{background-color:#000080;}td[bgcolor="#0000BF"]{background-color:#0000BF;}
  td[bgcolor="#0000FF"]{background-color:#0000FF;}td[bgcolor="#004000"]{background-color:#004000;}td[bgcolor="#004040"]{background-color:#004040;}td[bgcolor="#004080"]{background-color:#004080;}
  td[bgcolor="#0040BF"]{background-color:#0040BF;}td[bgcolor="#0040FF"]{background-color:#0040FF;}td[bgcolor="#008000"]{background-color:#008000;}td[bgcolor="#008040"]{background-color:#008040;}
  td[bgcolor="#008080"]{background-color:#008080;}td[bgcolor="#0080BF"]{background-color:#0080BF;}td[bgcolor="#0080FF"]{background-color:#0080FF;}td[bgcolor="#00BF00"]{background-color:#00BF00;}
  td[bgcolor="#00BF40"]{background-color:#00BF40;}td[bgcolor="#00BF80"]{background-color:#00BF80;}td[bgcolor="#00BFBF"]{background-color:#00BFBF;}td[bgcolor="#00BFFF"]{background-color:#00BFFF;}
  td[bgcolor="#00FF00"]{background-color:#00FF00;}td[bgcolor="#00FF40"]{background-color:#00FF40;}td[bgcolor="#00FF80"]{background-color:#00FF80;}td[bgcolor="#00FFBF"]{background-color:#00FFBF;}
