css:
@-moz-document domain("mokum.place") {
body > div.container > div.row.header-row > div > div > div.title > h1 > a {}
.box-header,
.box-header-empty,
.box-header-timeline,
.box-header-friends,
.box-header-groups {
    border: none;
}
body {
    background-color: #F2F2F2;
    background-image : url(http://i.imgur.com/0tuzPc9.png);
}
.content .box-body {
    background-color: #FFDD94;
    border-radius: 10px;
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
}
.sidebar .box-body {
    background-color: #FFDD94;
    border-radius: 10px;
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
}
div.box-header-timeline {
    font-size: 16px;
}
li.previous a,
li.next a {
    background-color: #FFC566;
    color: #000000;
}
.comment-text {
    color: #000000;
}
#live > div > div > div.post-body > div.title > span {
    color: #000000;
}
.nav-pills > li.active > a {
    background-color: #FFB53D;
}
