css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.feedly.com/home"), url-prefix("https://www.feedly.com/home"),
               url-prefix("http://feedly.com/index.html"), url-prefix("https://feedly.com/index.html"),
               domain("cloud.feedly.com"), domain("feedly.com") {

* 
{
font-family: tahoma!important;
text-decoration: none!important;
}
     
.source,
.engagement.no-icon,
.summary,
.condensed-tools,
.header.row.kickered,
#mytab_header,
#feedlyTabsPin,
.pinButton,
.fx-button.secondary,
#integrationstab_header,
#librarytab_header,
.label.primary.iconless,
#latesttab_header,
#headerBarFX,
h4,
#tagList,
#addContentPlaceholderFX,
.icon.icon-md.icon-tertiary.icon-fx-feedly-android-md-black.feedly-logo,
.small.pro.primary,
.giant-mark-as-read.full-width.secondary,
.hint.centered,
.col-xl-3.xlarge-only
{
display: none !important;
visibility: hidden;
}
      
.entry.quicklisted.read
{
height:63px !important;
line-height:63px !important;
padding-right:0px !important;
vertical-align:middle !important;
text-align:left !important;
}
                 
.content
{
height:63px !important;
line-height:63px !important;
vertical-align:middle !important;
text-align: center !important;
border: 0px solid #bbbbbb;
border-radius: 0px !important;
color: #aaa;
}
                             
.save-for-later.saved 
{
width: 300px !important;
float: right !important;
padding-right:0px !important;
height:63px !important;
background-color: #000!important;
/* background-image:  url(http://i.imgur.com/OyDqIkz.png) !important; */
margin: 0px !important;
border: 1px solid #bbbbbb;
border-radius: 0px !important;
color: #aaa !important;
}
                 
.save-for-later
{
width: 300px !important;
float: right !important;
padding-right:0px !important;
height:63px !important;
background-color: #424242!important;
/* background-image:  url(http://i.imgur.com/OyDqIkz.png) !important; */
margin: 0px !important;
border: 1px solid #bbbbbb;
border-radius: 0px !important;
color: #000 !important;
}
      
.title 
{
font-size: 20px !important;
color:black !important;
padding-right:20px !important;
padding-left: 10px !important;
line-height: 18px !important;
height: 56px !important;
text-align: center !important;
font-family: arial !important;
font-weight:  bold !important;
}
  
.entry.read.u0
{
height: 63px !important;
}
                 
#searchBarFX
{
height: 30px !important;
}              

/* DIMENSÕES DAS IMAGENS NAS PÁGINAS INDIVIDUAIS */
.visual
{
height: 150px !important;
width: 150px !important;
border-radius: unset !important;
}     
  
/* ALTURA DE CADA LINHA NAS PÁGINAS INDIVIDUAIS */
.entry.read.u4.density-29,
.entry.unread.u4.density-29
{
height: 150px !important;
}  
                 
/* ESCONDER ELEMENTOS INÚTEIS NOS POSTS DAS PÁGINAS INDIVIDUAIS */
.entry.unread.u4.density-29 .save-for-later
{
display: none !important;
visibility: hidden;
}

.engagement,
.board.button-icon-only-micro.icon.icon-fx-star-save-ios-md-black.tertiary,
.mark-as-read, .hide,
.header.row,
.col-xs-12.col-md-12.col-lg-12.col-xl-9 .save-for-later
{
display: none !important;
visibility: hidden;
}

.col-xs-12.col-md-12.col-lg-12.col-xl-9 .content,
.col-xs-12.col-md-12.col-lg-12.col-xl-9 .title,
.col-xs-12.col-md-12.col-lg-12.col-xl-9 .metadata              
{
margin-left: 0px !important;
text-align: left !important;
padding: 0px !important;
vertical-align: top !important;
margin: 0px !important;
}
                                        
}
