css:
@-moz-document url-prefix("http://totalwar-turkiye.com/") {
#header{background: #2a2e31 !important;}
#header div.frame{background: #2a2e31 !important;}
a:hover {text-decoration: underline !important;}
a:link, a:visited{color: #b3e2e3 !important;}
#main_menu{padding: 6px !important; background: #7f7f7f !important;}
.dropmenu li a.active span.firstlevel{background: none !important; color: white !important;}
.dropmenu li a.active{background: none !important}
.dropmenu li a.firstlevel:hover span.firstlevel, .dropmenu li:hover a.firstlevel span.firstlevel{background: none !important;}
.dropmenu li a.firstlevel:hover, .dropmenu li:hover a.firstlevel{background: none !important;}
.dropmenu li li a:hover, .dropmenu li li:hover>a{background: #7F7F7F !important; color: white !important;}
.dropmenu li ul{background: #7F7F7F !important; }
div#upper_section div.user ul li{color: wheat !important;}
#content_section{background: #2a2e31 !important;}
#content_section div.frame{background: #2a2e31 !important;}
.buttonlist ul li a.active span {background: #7F7F7F !important;}
.buttonlist ul li a.active{background: #7F7F7F !important;}
.buttonlist ul li a{background: #7F7F7F !important;}
.buttonlist ul li a span{background: #7F7F7F !important;}
h4.catbg, h4.catbg2, h3.catbg, h3.catbg2, .table_list tbody.header td.catbg{background: #424649 !important;}
div.cat_bar{background: #424649 !important;}
.windowbg, #preview_body {color: wheat !important; background-color: #35393c !important;}
.windowbg span.topslice span{background: #35393c !important}
.windowbg span.topslice{background: #35393c !important}
.bbc_link:hover {text-decoration: underline !important; color: #9ac3c4 !important;}
.bbc_link:link, .bbc_link:visited {color: #b3e2e3; text-decoration: none;}
a:link, a:visited {color: #b3e2e3 !important;}
.signature, .custom_fields_above_signature{border-top: 1px solid #535659 !important;}
.windowbg span.botslice span{background: #35393c !important;}
.windowbg span.botslice{background: #35393c !important;}
.bbc_link:link, .bbc_link:visited{border-bottom: none !important;}
.windowbg2 {color: wheat !important; background-color: #35393c !important; border-bottom: 2px solid #2a2e31 !important;}
.windowbg2 span.topslice{background: #35393c !important;}
.windowbg2 span.topslice span{background: #35393c !important;}
.windowbg2 span.botslice span{background: #35393c !important;}
.windowbg2 span.botslice{background: #35393c !important;}
blockquote.bbc_standard_quote{background-color: #404447 !important;}
blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote{color: #999 !important; padding: 15px !important; border: 1px solid #5a5a5a !important;}
.description, .description_board, .plainbox{background: #424649 !important; border: 1px solid #5a5a5a !important;}
span.upperframe{background: #2a2e31 !important;}
span.upperframe span{background: #2a2e31 !important;}
.roundframe{background: #2a2e31 !important; border: 1px solid #424649 !important;}
span.lowerframe span{background: #2a2e31 !important;}
span.lowerframe{background: #2a2e31 !important;}
#footer_section div.frame{background: #2a2e31 !important;}
#footer_section{background: #2a2e31 !important;}
dl#ic_recentposts dt{color: #8a8a8a !important;}
dl#ic_recentposts dd{color: #8a8a8a !important;}
div.title_barIC h4.titlebg{background: #424649 !important;}
div.title_barIC{background: #424649 !important;}
body, td, th, tr {color: #8a8a8a !important;}
.lockedbg2{background: #737373 !important}
table.table_grid td{    border-top: 2px solid #2a2e31 !important; border-right: 2px solid #2a2e31; border-bottom: none !important;}
.stickybg2{background-color: #4a4d50;}
.stickybg{background-color: #4a4d50;}
.lockedbg{background: #737373 !important;}
.table_frame .table_list td.icon, .table_frame .table_list td.info, .table_frame .table_list td.stats{    border-right: 2px solid #2a2e31 !important;}
.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th{background: #424649 !important;}
.titlebg, .titlebg2, tr.titlebg th, tr.titlebg td, tr.titlebg2 td{background: #424649 !important;}
.table_grid tr.catbg, .table_grid tr.titlebg{border-bottom: 1px solid #35393C !important;}
.table_list tbody.content td.children{border-bottom: 2px solid #2A2E31 !important;}
.poster{text-align: center !important;}
}
