@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("waffle.io") {
  /*! Waffle.io Dark Blues Theme v1.0.3 (01-Aug-2016) */
  /*
 * https://github.com/doublehelix/Stylish-Themes
 * http://userstyles.org/styles/
 * License: http://creativecommons.org/licenses/by/4.0/
 */
  /****************************************/
  /* Landing/Home Page and Base Styles    */
  /****************************************/
  body {
    color: #fff;
  }
  body.landing {
    color: #333;
  }
  body #page {
    background-color: #283030;
  }
  #light-header {
    background-color: #443344;
    box-shadow: 0px 3px 100px 20px rgba(0, 0, 0, 0.15);
  }
  .home-page {
    background: #283030;
  }
  .home-page .user-panel {
    background: #485050;
    box-shadow: 0 0 12px #000;
  }
  a {
    color: #5CB85C;
  }
  a:hover,
  a:focus {
    color: #449D44;
  }
  .project-list-container .project-list-item {
    background: #484850;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.3);
  }
  .project-list-container .project-list-item:hover,
  .project-list-container .project-list-item:focus {
    background: #60606b;
    box-shadow: 1px 3px 8px rgba(0, 0, 0, 0.8);
  }
  .repo-icon {
    color: #fff;
  }
  .btn-pin.pinned i {
    color: #5CB85C;
  }
  .btn-pin.pinned:hover i {
    color: #449D44;
  }
  /***** REPO CHOOSER *****/
  .github-repo-chooser {
    background-color: #484850;
  }
  .github-repo-chooser .sources-container {
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.3);
  }
  .github-repo-chooser .sources-container .source {
    border-top: 1px solid #e6e6e6;
    color: #fff;
  }
  .github-repo-chooser .sources-container .source:hover {
    background-color: #60606b;
    color: #fff;
  }
  /****************************************/
  /* waffle brand icon */
  /****************************************/
  .sidebar.navbar .header-nav .navbar-brand-ct .navbar-brand {
    background: #443355 none repeat scroll 0% 0%;
    border-bottom: 1px solid #525252;
  }
  .sidebar.navbar .header-nav .navbar-brand-ct .navbar-brand i {
    color: #fff;
  }
  /****************************************/
  /* Header nav colors */
  /****************************************/
  .sidebar.navbar .header-nav {
    background: #182222;
    border-bottom: 1px solid #CDCDCD;
  }
  /****************************************/
  /* board title */
  /****************************************/
  .dropdown-title {
    color: #bbb;
  }
  .dropdown-title:hover {
    color: #fff;
  }
  /****************************************/
  /* board background */
  /****************************************/
  .board .board-body {
    background-color: #283030;
    white-space: nowrap;
    overflow-x: auto;
  }
  /****************************************/
  /* Header Buttons */
  /****************************************/
  .header-nav button.primary,
  .header-nav .button.primary {
    background-color: #443355;
  }
  .sidebar.navbar .header-nav .board-actions .add-new-issue .button {
    border: 1px solid #659;
    color: #fff;
  }
  .header-nav button.primary:hover,
  .header-nav .button.primary:hover,
  .header-nav button.primary:focus,
  .header-nav .button.primary:focus {
    background-color: #49A6EE;
  }
  .header-nav button:hover,
  .header-nav .button:hover,
  .header-nav button:focus,
  .header-nav .button:focus {
    background-color: #B3B3B3;
    color: #fff;
  }
  /****************************************/
  /* Columns */
  /****************************************/
  .board .board-body .column-ct .column {
    border-right: 4px solid #455;
  }
  .board .board-body .column-ct .column .column-header {
    background-color: #443344;
  }
  /****************************************/
  /* CARDS */
  /****************************************/
  .board .card {
    border-radius: 3px;
    box-shadow: 0 0 0.4em rgba(0, 0, 0, 0.6);
    transition: box-shadow 1s ease 0s;
  }
  .board .card:hover {
    box-shadow: 0 0 0.4em rgba(96, 255, 96, 0.9);
  }
  .board .card .card-header {
    border-radius: 3px 3px 0px 0px;
    background-color: #223333;
  }
  .board .card .card-body {
    border-radius: 0px 0px 3px 3px;
    background-color: #484850;
  }
  .board .card .card-body .title,
  .board .card .card-body textarea.title {
    background-color: transparent;
    color: #cdd;
    border-radius: 3px;
    border: 1px solid transparent;
  }
  .label-pill {
    color: #222;
  }
  .label-pill.light {
    text-shadow: 0px 1px 0px rgba(255, 255, 255, 0.4);
  }
  .label-pill.dark {
    color: #fff;
    text-shadow: 0px -1px 0px rgba(0, 0, 0, 0.2);
  }
  /****************************************/
  /* Settings                             */
  /****************************************/
  .settings-page {
    background-color: #283030;
  }
  .popover {
    color: #333;
  }
  .well {
    background-color: #484850;
    border: 1px solid #CDCDCD;
    border-radius: 4px;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.2) inset;
    color: #fff;
  }
  /****************************************/
  /* Metrics                              */
  /****************************************/
  .waffle-metrics {
    background-color: #283030;
  }
}
/*# sourceMappingURL=../dist/waffle-dark-blues.css.map */
