css:
@-moz-document regexp("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/edit.html.*") {
body {overflow:hidden !important}
body, #stylish-edit {background:#2C3E50 !important; color:#ECF0F1 !important; font-family:Open Sans, Segoe UI !important}
h1, h2 {font-weight:100 !important}

#basic-info-name {height:30px !important}
input, select {height:30px !important; padding:5px 10px !important; background:#2C3E50 !important; border:solid 1px #34495E !important; color:#ECF0F1 !important}
#basic-info-enabled {margin:10px 0 !important}
input[type="checkbox"] {height:15px !important; width:15px !important; margin:0 5px !important}
label {font-size:13px !important; vertical-align:middle !important}
button {width:49% !important; background:#2C3E50 !important; height:30px !important; line-height:12px !important; color:#ECF0F1 !important; padding:9px 0 !important; border:solid 1px #34495E !important; font-size:13px !important; margin:2px 0 !important}
button[disabled] {color:#7F8C8D !important}
#options .option label {vertical-align:super !important; display:inline !important}
#options [type="number"] {width:auto !important; max-width:50px !important}
.aligned {display:block !important; margin:10px 0 0 0 !important}
.aligned label {position:relative !important; vertical-align:middle !important}
.aligned select {float:right !important}

#sections h2 {margin:22px 0 18px 0 !important}
#sections > div > label {display:none !important}
#sections > div {margin:0 !important; padding:0 15px 0 0 !important}
.CodeMirror {background:#2C3E50 !important; border:solid 1px #34495E !important; color:#ECF0F1 !important; height:75% !important}
.CodeMirror-activeline-background {background:rgba(0,0,0,.2) !important}
.cm-s-default .cm-tag {color:#2ECC71 !important}
.cm-s-default .cm-qualifier {color:#3498DB !important}
.cm-s-default .cm-builtin {color:#2980B9 !important}
.cm-s-default .cm-atom {color:#1ABC9C !important}
.cm-s-default .cm-keyword {color:#E67E22 !important}
.cm-s-default .cm-number {color:#95A5A6 !important}
.cm-s-default .cm-string {color:#F1C40F !important}
.CodeMirror-gutters {background-color:#34495E !important; border:none !important}
.CodeMirror-linenumber {color:#ECF0F1 !important}
button.remove-applies-to, button.add-applies-to {width:auto !important; text-align:center !important; padding:0 10px !important; margin:0 0 0 15px !important}
button.remove-section, button.add-section, button.beautify-section {width:auto !important; text-align:center !important; padding:0 10px !important; margin:0 15px 0 0 !important}

#help-popup {background-color:#2C3E50 !important; padding:0 !important; box-shadow:#000 0 0 5px !important}
#help-popup .title {background:#2C3E50 !important; margin:0 !important}
#help-popup .close-icon {background: linear-gradient(-45deg, transparent 5px, #ECF0F1 5px, #ECF0F1 6px, transparent 6.5px) no-repeat, linear-gradient(45deg, transparent 5px, #ECF0F1 5px, #ECF0F1 6px, transparent 6.5px) no-repeat !important}
}
