@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("aircalc.net") {
body {
	background-color: #222222;
	color: #C0C0C0;}
PresetTable {
	background-color: #222222;
	color: #C0C0C0;}
th {
	background-color: #222222;
	color: #C0C0C0;}
.ResultOption {display:none !important;}
#InputFighterTable {display:none !important;}
.fighter-info{display:none !important}
li{display:none !important}
address{display:none !important}
#ShortURLText{display:none !important}
#ShortURLMessage{display:none !important}
#Tsubuyaku{display:none !important}
.header{background-color: #222222;}
.result-table .header .name, .result-table .header .slot {
    background-color: #222222;
}
.result-table .header .upward, .result-table .header .fire, .result-table .header .effective-power {
    background-color: #222222;}
.area-table .current-mastery, .area-table .diff {
	background-color: #222222;}
}
