css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.e-shop.gr") {
body {
    background-image:url("http://www.pixeden.com/media/k2/galleries/44/003-metal-and-carbon-fiber-pattern-background-texture.jpg");
}
.web-footer-links-section {
    background-image:url("http://www.pixeden.com/media/k2/galleries/44/003-metal-and-carbon-fiber-pattern-background-texture.jpg");
}
.web-product-title {
  background-color: rgba(0, 0, 0, 0.42);
  border-bottom: 2px solid #1707B6;
}
 .web-product-num {
    background: transparent;
 }
.web-product-price {
  background-color: rgba(0, 0, 0, 0.42);
  border-bottom: 2px solid #1707B6;
}
.web-product-info {
  background-color: rgba(0, 0, 0, 0.42);
  color: #00D2FF;
}
.web-product-buttons {
  background-color: rgba(0, 0, 0, 0.42);
}
.web-filter-titles-text {
    color: #99E6FF;
  border-right: 1px solid #1F4187;
border-bottom: 1px solid #F22727;
}
.web-filters-heading {
  color: rgb(234, 234, 234);
background-color: rgba(0, 0, 0, 0.54);
  border-color: #600000;
  background-image: url("https://www.colourbox.com/preview/2260820-blue-fire.jpg");

}
.web-filter-yellowGraphic {
  display: none;
}
.web-filters-list {
  border-bottom: 1px solid #090A62;
}
.web-filter{
  color: #4E93CE;
}
#navigation_vert ul li {
    background: rgba(0, 0, 89, 0.3) none repeat scroll 0% 0% !important;
    border-radius: 50%;
  height: 40px;
border-bottom: 2px solid #3116EA !important;
}
  #navigation_vert ul li:hover {box-shadow: 0px 2px 14px -4.7px #2C0DFD, 0px 1px 3px #DB00FC !important; }
  #web_menu {
   background: transparent none repeat scroll 0% 0%;
  }
.sidebar {
  display: none;
}
  #navigation_vert ul li.menuinline {
    background: rgba(0, 0, 89, 0.3) none repeat scroll 0% 0% !important;
    border-bottom: 2px solid #3116EA;
}

div#web_header > table {
    background: #00D7D7 none repeat scroll 0% 0%;
  background-color: #000506;
  background-image:url("https://www.colourbox.com/preview/2260820-blue-fire.jpg");
}
.web-filters-wrap:hover {
    box-shadow: 0px 2px 14px -4.7px #2C0DFD, 0px 1px 3px #DB00FC;
}
.web-filters-wrap {
  border-width: 0px 1px 1px;
  border-color: -moz-use-text-color #F30505 #F22626;
  background-image: none;
  background-color: rgba(5, 0, 0, 0.67);
}
.web-product-container {
  background-image: url("http://www.pixeden.com/media/k2/galleries/44/003-metal-and-carbon-fiber-pattern-background-texture.jpg");
}
 .web-product-container:hover {
   box-shadow: 0px 2px 14px -4.7px #2C0DFD, 0px 1px 3px #DB00FC !important;
 }
 div#right_panel > P {
    display: none;/*the ads on left panel*/
}
  .web-title-link {
    color: #E40000 !important;
  }
.web-subheader-actions {
    background-color: rgba(3, 254, 192, 0.39);
    border-radius: 10%;
}
  .web-subheader-links {
    display: none;
}
  .web-subheader-triangle-right {
    display: none;
  }
  .web-subheader-breadtext {
    color: #B72323 !important;
 }
.web-header-dock {
   display: none !important;
  }
 div#web_header td {
    border-radius: 0px 20px 30px;
}
  #web_footer_banner {
    display: none;
  }
  .web-footer-newsletter-section {
    display: none;
  }
  td.web-product-photo img {
    border: 4px solid;
    border-radius: 20%;
}
  .web-footer-content {
    color: #9C9DF9;
  }
  .web-footer-links-content a {
    color: #FF5B5B;
  }
  .menu_titlecell {
    background: rgba(10, 0, 164, 0.69) none repeat scroll 0% 0%;
  }
  .menu_titlecell2 {
    background: rgba(10, 0, 164, 0.69) none repeat scroll 0% 0%;
  }
  .menu_earcell {
    display: none;
}
  .menu_eartxtcell {
    background: transparent;

  }
  .menu_bodycell {
    background: rgba(4, 18, 44, 0.68) none repeat scroll 0% 0%;
  }
  #navigation_vert .dropdown {
    color: #65C2D7;
  }
  .menu_bottomcell {
    background: transparent;
  }
  a.menu_link_small {
color: #4BE6DB !important;
  }
  a.menu_link {
    color: #19E3E3 !important;
  }
  td.menu_bodycell > table > tbody > tr > td:nth-child(2) {
    border-left: 2px solid #2636E3 !important;
}
  div.header_container {
   border-bottom: 3px solid #FF0900;
}
  .crazy-container {
   box-shadow: 0px 1px 5px -1px #D20B0B, 0px 0px 33px -5px #BC1212;
}
  .crazy-container:hover {
  box-shadow: 0px 1px 5px -1px #4729BD, 0px 0px 33px -5px #127ABC !important;
}
  .crazy-description-row p {
 color: #A7B4F0;
}
  td.crazy-title-cell > a.main {
    color: #E60D0D !important;
}
  .crazy-price-wrapper {
   color: blue;
}
  div.dropdown > table > tbody > tr:nth-child(2) > td:first-child {
    background-color: blue;
  }
 .crazy-row {
    background-color: rgba(2, 2, 60, 0.3);
}
  .crazy-title-row {
    background-image: url("https://www.colourbox.com/preview/2260820-blue-fire.jpg");
}
  td.crazy-col-left img {
    border-radius: 20%;
    border: 4px solid;
}
  td.shop_table_title {
    color: #DEDEDE;
    background: transparent url("https://www.colourbox.com/preview/2260820-blue-fire.jpg") repeat scroll left top;
}
  td.links_table_title {
    border-top: 1px solid #03E74E;
    background: rgba(22, 43, 116, 0.49) none repeat scroll 0% 0%;
    color: #70E1F3;
  }
  td.links_table_links {
    border-top: 1px solid #1484B9;
    background: rgba(44, 7, 131, 0.46) none repeat scroll 0% 0%;
  }
  .white_linkt {
    color: #2697F6 !important;
  }
  td.product_table_body {
    border: 1px solid #5711F6;
    color: #48CDD8;
    box-shadow: 0px 2px 14px -4.7px #60CECE, 0px 12px 33px #2B26FF !important;
    background: rgba(12, 12, 92, 0.53);
}
}
