css:
@-moz-document url-prefix("http://www.instasync.com/r/"), url-prefix("http://instasync.com/r/") {
.col-sm-4.col-xs-12.column .chat {
	color: #ccc;
}
.msg-timestamp {
	color: #4a4a4a;
}
#chat_messages .message.chat-modAction {
    color: #54B36E;
}

#chat_messages .message.chat-modAction .modName {
    color: #A9ECBC;
}
.room-container .room-component-row .chat #tabs_chat_content .user-list {
    background-color: rgba(64,64,64,0.8);
}
.room-container .room-component-row .chat #tabs_chat_content .user-list li.mod {
	color: #D5DAF0!important;
}
.room-container .room-component-row .chat #tabs_chat_content .user-list li.registered {
    color: #aaa;
}
.room-container .room-component-row .chat #tabs_chat_content .user-list li.unregistered {
	color: #6a6a6a;
}

.room-container .room-component-row .chat #tabs_chat_content .user-list li {
    color: #999;
}

.room-container .room-component-row .chat #tabs_chat_content .user-list li:hover {
    background-color: #555;
}

#cin {
    background-color: #202020;
    color: #ddd;
    border-top:1px solid #303030!important;
}

.container {
    width: 1860px!important;
}

.room-component-row .chat .chat-tabs-content {
    height: 662px!important;
}
.chat #chat_messages .chat-mention {
    background-color: #882828!important;
}

.chat #chat_messages .mod-message {
    color: #1079D4!important;
}

/*background of page container*/
body.dim {
	background-color: #000;
}

#media {
	height: 697px;
}
#media .twitch-video {
	height: 698px;
}

/*background color*/
body {
	background: black!important;
	background-image: none;
}

/*top bar background, border, shadow*/
body.dim .navbar-default {
	background-image: -webkit-linear-gradient(top,#111 0,#161616 100%);
	background-image: -o-linear-gradient(top,#111 0,#161616 100%);
	background-image: -webkit-gradient(linear,left top,left bottom,from(#111),to(#161616));
	background-image: linear-gradient(to bottom,#111 0,#161616 100%);
	background-repeat: repeat-x;
	border-radius: 4px;
	-webkit-box-shadow: inset 0 1px 0 rgba(25,25,25,.15),0 1px 5px rgba(0,0,0,.15);
	box-shadow: inset 0 1px 0 rgba(25,25,25,.15),0 1px 5px rgba(0,0,0,.15);
	border-color: #222;
	background-color: #161616;
}

/*icon in top left: on hover*/
.navbar .navbar-brand:hover {
	opacity: .8;
}

/*top navbar text colors and text shadows*/
.navbar-default .navbar-brand, .navbar-nav>li>a, .navbar-default .navbar-nav>li>a {
	color: #BBB;
	text-shadow: 0 1px 0 rgba(150,150,150,.35);
}

/*top navbar text colors: on hover, on focus*/
.navbar-default .navbar-nav>li>a:hover, .navbar-default .navbar-nav>li>a:focus {
	color: #777;
	background-color: transparent;
}

/*footer background color*/
body.dim .footer {
	background-image: none;
	background-color: #111;
}

/*outer playlist bg color*/
body.dim .room-container .room-component-row .video-controls {
	background-color: #171717;
	color: #ddd;
}

/*inner playlist bg color (beneath playlist videos)*/
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist {
	background-color: #1c1c1c;
}

/*area above playlist*/
.room-container .room-component-row .video-controls {
	border-right: 1px solid #303030;
}

/*main page content background*/
.room-container .room-component-row {
	background-color: #111;
}

.room-container .room-component-row .chat {
	border-bottom: 1px solid #303030;
}

.room-container .room-info-row {
  background: #111;
  color: silver;
  border-top: 1px solid #303030;
}

.room-container .room-info-row .overview .room-stats {
  color: #777;
}

.navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:hover, .navbar-default .navbar-nav>.open>a:focus {
	background-color: #444;
	color: #ccc;
}

/*playlist border color*/
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist {
	border: 1px solid #333;
}

/*video text color: title, time. also, border*/
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li {
	border-bottom: 1px solid #333;
	color: #DDD;
	background-color: #242424;
}

/*active video text color: title, time || Add Video color*/
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active, .room-container .room-component-row .video-controls .nav-tabs li a {
	color: #DDD;
}

.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li .pl-video-info .addedby a {
	color: #888;
}

.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active a {
	color: #aaa;
}
.room-container .room-component-row .chat #tabs_chat_content .chat-messages .chat-message .message:hover {
	background-color: #3A6E90;
}
/*active video background color*/
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active {
	background-color: #343434;
}

/*video hover*/
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li:hover {
	background-color: #393939;
}

#tabs_users_content.tab-pane.active, #tabs_chat_settings_content.tab-pane.active {
	color: #ddd;
}

/*chat background*/
.room-container .room-component-row .chat #tabs_chat_content .chat-messages {
	background-color: #202020;
}

/*don't know. intentions were to change the border above playlist area, looks like a general class name?*/
.nav-tabs {
	border-bottom: 1px solid #303030;
}

body.dim .room-container .room-component-row .video-controls .tab-content {
	background-color: #1e1e1e;
}
.tab-content>.active {
  background-color: #1e1e1e;
}

/*chat tab container*/
ul.nav.nav-tabs.chat-tabs {
	background-color: #171717;
}
/*selected playlist tabs: default, on hover, on focus*/
.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
	background-color: #1e1e1e;
	border-color: #303030;
	border-bottom: 1px solid #1e1e1e;
}

/*unselected playlist tab: on hover, on focus*/
.nav-tabs>li>a:hover, .tabs-users:hover, .tabs-users:focus {
	border-color: #444 #444 #444;
	background-color: #212121;
}

#poll_column .poll.active {
    background-color: rgba(204, 204, 204, .9);
    color: black;
}

#poll_column .poll {
	border-radius: 3px;
    background-color: rgba(204, 204, 204, .1);
    opacity: 1;
    color: #9a9a9a;
}
li.tabs-users.visible {
	background-color: #2b2b2b!important;
}

#poll_column .poll.active .poll-votes {
	background: -webkit-gradient(linear, 0% 40%, 0% 70%, from(#f9f9f9), to(#e3e3e3));
    background: -moz-linear-gradient(center top, #f9f9f9, #e3e3e3) repeat scroll 0 0 transparent;
}

#poll_column .poll .poll-votes {
	border-radius: 3px;
    padding: 2px 7px;
}
#add_video_url {
background-color: #1e1e1e;
  color: #ddd;
  border-color: #222;
}
.nav-tabs>li>a:focus {
	background-color: #2b2b2b;
}

i {
	color: #888;
}

i:hover {
	color: #666;
}

.nav .open>a, .nav .open>a:hover, .nav .open>a:focus {
	background-color: #444;
}
.room-container .room-component-row .video-controls .nav-tabs li.add-video .dropdown-form {
	background-color: #aaa;
}

.room-container .room-component-row .chat .chat-tabs li a:hover {
	border-top: 1px solid #444;
}

.room-container .room-component-row .chat .chat-tabs li a {
	border-top: 1px solid transparent;
	border-radius:4px 4px 0 0;
}

.room-container .room-component-row .chat .chat-tabs li.active a {
	border-top: 1px solid #303030;
}

.room-container .room-component-row .chat .chat-tabs li.tabs-users:hover, .room-container .room-component-row .chat .chat-tabs li.tabs-users.visible {
	background-color: #333;
}
}
