css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ubuntuforums.org"), domain("login.ubuntu.com") {
  /* ============ Default Reset ============ */
  
  header .wrapper { background: transparent none !important; } /* login.ubuntu.com */
  * { background-color: transparent !important; } /* */
  
  * { border-color: #00ACAF !important; color: #777 !important; box-shadow: none !important; text-shadow: none !important; } /* */
  a:link { color: #00ACAF !important; } /* */
  a:visited { color: #00ACCF !important; } /* */
  
  /* img fix */
  .above_body { background: none !important; }
  
  /* ============ Full Site Effect ============ */
  
  html, body { background: #000C17 none !important; } /* site background */
  div.above_body { background-color: #000C27 !important; } /* top nav pane */
  
  .blocksubhead { color: #00ACAF !important; }
  
  /* thread tools */
  .toolsmenu { border: none !important; }
  
  /* pagination */
  .pagination span.prev_next a, .pagination span.first_last a { top: -1px !important; }
  .pagination span:hover { background-color: #000CC7 !important; }
  /* .pagination span a { padding: 0px 4px !important; } /* new style */
  .pagination span.selected a { color: red !important; border: 1px solid red !important; }
  
  /* popup body */
  .popupbody { background-color: #000C17 !important; border: 1px solid #00ACAF !important; } /* popup background */
  .popupbody li[id*="link_"]:hover, .popupbody li > a:hover { background-color: #000C47 !important; } /* popup highlight */
  
  .cke_dialog { background-color: #000C17 !important; } /* smileys picker */
  
  .smilielist li { border: 1px solid #00ACAF !important; }
  div.smilie { border: none !important; }
  .smilielist li:hover { background-color: #000C47 !important; }
  .smilielist .label { border: none !important; }
  
  .cke_button .restoretext { color: #00ACAF !important; }
  .cke_button .restoretext:hover { background-color: #000C47 !important; }
  
  .cke_dialog_title { color: #00ACAF !important; }
  .cke_dialog_ui_button { color: #00ACAF !important; }
  .cke_dialog_ui_button:hover { background-color: #000C47 !important; }
  a[class*="cke_button_"] span { color: #00ACAF !important; }
  a[class*="cke_button_"]:hover { background-color: #000C47 !important; }
  div.cke_dialog_ui_input_text { border: 1px solid #00ACAF !important; }
  li.cke_panel_listItem a { border-color: transparent !important; }
  li.cke_panel_listItem:hover { background-color: #000C47 !important; }
  .cke_panel_grouptitle { color: #00ACAF !important; }
  #smilies .blocksubhead { background-color: #000C27 !important; }
  
  /* quick nav popup */
  .navpopupbody { width: 350px !important; }
  .navpopupbody li.optionlabel { background-color: #000C47 !important; }
  .navpopupbody li a:hover { background-color: #000C47 !important; }
  
  /* bread crumbs */
  .breadcrumb .navbit > a:hover, .breadcrumb .navbit.lastnavbit span:hover { background-color: #000C47 !important; }
  .breadcrumb .navbit.lastnavbit span { padding-right: 3px !important; border: 1px solid #777 !important; } /* adjust last breadcrumb width */
  
  /* input text boxes */
  input, select, select > option { color: #00ACAF !important; }
  .formcontrols input.textbox:focus { background-color: transparent !important; }
  
  /* ============ Buttons ============ */
  
  .button { background: transparent none !important; }
  
  /* nav btns */
  .loginbutton, .loginbutton:link, .loginbutton:visited, .cta { background: transparent none !important; } /* login button */
  .loginbutton:hover, .cta:hover { background-color: #000C47 !important; }
  
  * html .navpopupmenu.popupmenu.nohovermenu a.textcontrol, .navpopupmenu a.textcontrol, .navpopupmenu a.popupctrl, .navpopupmenu.popupmenu.nohovermenu a.textcontrol, .navpopupmenu.popupmenu.nohovermenu a.popupctrl { background: transparent none !important; }
  .cke_skin_kama .cke_dialog_contents a.cke_dialog_ui_button_ok, .cke_skin_kama .cke_dialog_contents a.cke_dialog_ui_button_cancel, .textcontrols a, a.textcontrol { background: transparent none !important; }
  
  .cke_skin_kama .cke_editor .cke_rcombo a, .cke_skin_kama .cke_editor .cke_toolgroup { background: transparent none !important; } /* compose text */
  .cke_skin_kama .cke_toolgroup { background: transparent none !important; }
  .cke_skin_kama .cke_wrapper, .cke_skin_kama .cke_wrapper.cke_ltr, .cke_skin_kama .cke_wrapper.cke_rtl { background: transparent none !important; }
  
  .ctrlcontainer { color: #00ACAF !important; } /* quick nav btn text */
  
  .newcontent_textcontrol, .actionbuttons .group .button { border: 1px solid #00ACAF !important; background: none !important; } /* new thread */
  .newcontent_textcontrol:hover, .actionbuttons .group .button:hover { background-color: #000C47 !important; }
  
  input.button { color: #00ACAF !important; border: 1px solid #00ACAF !important; } /* adv search page */
  input.button:hover { background-color: #000C47 !important; }
  
  /* button highlight - some */
  a.textcontrol:hover, a.popupctrl:hover { background-color: #000C47 !important; }
  
  /* ============ Nav Pages ============ */
  
  li[id*="thread_"]:hover { background-color: #000C47 !important; } /* thread line highlight */
  
  /* ============ Thread Posts ============ */
  
  .thread_info .blockbody { background: transparent none !important; }
  
  div#pagetitle { padding: 10px 0px 0px !important; }
  .bbcode_code, .bbcode_quote { background-color: #000C17 !important; }
  
  li[id*="post_"]:hover { background-color: #000C47 !important; }
  .posthead, .userinfo, .userinfo_noavatar { background-color: #000C27 !important; }
  .userstats dt, .username span { color: #00ACAF !important; }
  a.firstunread { border: none !important; padding-top: 4px !important; }
  h2.posttitle { color: #00ACAF !important; }
  
  /* ============ Compose ============ */
  
  label { color: #00ACAF !important; }
  
  /* ============ Main Page ============ */
  
  .forumrow.table, .navlinks, .blockhead, .thread_info h4 { background-color: #000C27 !important; background-image: none !important; }
  .forumrow.table:hover { background-color: #000C47 !important; }
  
  /* ============ Profile Page ============ */
  .tabslight .userprof_moduleinactive { border-top-color: transparent !important; } /* profile categories - inactive */
  
  /* ============ Settings Page ============ */
  h5 { color: #00ACAF !important; }
  .blockhead { border: 1px solid #00ACAF !important; color: #00ACAF !important; } /* header border for settings groups */
  #usercp_nav .blockrow * a:hover { background-color: #000C47 !important; }
  
  li[id*="pm_"]:hover { background-color: #000C47 !important; }
  .pmlist .blockrow:first-child { border-top: 1px solid #00ACAF !important; }
  
  /* .blocksubhead { background-color: #000C27 !important; } /* */
  
  .vB_DatePicker.page { background-color: #000C17 !important; } /* date picker bg */
  .vB_DatePicker .tcat { background-color: #000C47 !important; color: #00ACAF !important; } /* date picker month top row */
  .vB_DatePicker > thead > tr.page.smallfont { background-color: #000C27 !important; } /* week row */
  
  .vB_DatePicker td.smallfont { color: #00ACAF !important; } /* day font color */
  .vB_DatePicker td.smallfont:hover { background-color: #000CC7 !important; }
  .vB_DatePicker td.time.smallfont, .vB_DatePicker td.tfoot.smallfont { color: #777 !important; } /* non-month day color */
  .vB_DatePicker td.today.time, .vB_DatePicker td.today { color: red !important; border: 1px solid red !important; } /* today color */
  
  .userprof_moduleinactive, #sidebar_container.member_summary, .moduleinactive_bg { background: transparent none !important; box-shadow: none !important; }
}
