css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.hotukdeals.com") {

  
/* Outer background color */ 

body, .hero-header, .footer-container {

  background-color: rgb(19,19,19) !important;

}

/* Inner background color */

.content-layout-container {

  background-color: rgb(23,23,23) !important;
  box-shadow: 0px 0px 5px 0px rgba(0,0,0,0.50) !important;

}

/* Inner background border */ 
  
.layout-mainContent-body {

  border-style: solid solid solid solid !important;
  border-width: 1px !important;
  border-color: rgb(0,0,0) !important;

}
  
.thread-buttonLink, .carousel-button--next, .carousel-button--previous, .button--color-greyBordered, .button--expanded, .paginationButton--arrow-prev, .paginationButton--arrow-next, .paginationButton, #comment-content, .listingHeader-tools-toggle, .thread-votingControl--down, .navDrop-toggle, .thread-actions-list, #sidebar-hottest-deals .link, #submitForm--submitButton, .voucherReveal-peel-bottom--masked, .subNav-toolbar, .modal-content-body.modal-content-body--small, .modal-content-headline.space--v-3.space--h-4, #confirm_email , #new_email, #confirm_password, #new_password, #current_password, #temp_threshold, .chosen-single, .select2-choices, .accordion-title.accordion-title--open, .accordion-title, #price, #description, #title, #deal_link, .space--v-4, .hukd-text--plain, #sidebar-top-unanswered-questions .link, .submitForm-optional-heading.hd-4 span, .navDrop-list, #bottom-keywords, .dummy, .tab {

  background-color: rgb(40,40,40) !important;
  border: 1px solid rgb(0,0,0) !important;
  box-shadow: 0px 0px 5px 0px rgba(0,0,0,0.15) !important;

}
  
.thread-title-text--link, .thread-title-text, .thread-additionalField-label, .hd-2, .sidebarCarouselWidget-title-tab-text, .sidebarListWidget-title-tab-text, .thread-buttonLink span, .thread-votingControl-text, .button--expanded, .size--all-l, .fGrid-left, #sidebar-hottest-deals .link, .comment-post-text-date , .comment-likes, .comment-position, .hide--toW3, .comment-tool-text, label, #submitForm--submitButton, .threadType-deals a, .action-ignore , .action-liked, .action-report, .action-reply, .threadType-feedback a, .hd--inline, .voucherReveal-peel-bottom-label, .comment-user-type-indicator, .you-like, .subNavMenu-link, .space--h-4, .space--fromW2-l-0, .noMsg, #confirm_email, #new_email, #confirm_password, #new_password, #current_password, .accordion-title-text, .space--l-2, .space--mb-3, #temp_threshold, .chosen-single, #s2id_autogen2, #s2id_autogen4, .hukd-label, .customiseTabPopup-header-title, .hd-4 span, .hd--noMargin, #sidebar-top-unanswered-questions .link, .submitForm-optional-heading.hd-4 span, .threadType-misc a, .threadType-for-sale-trade a, .threadType-competitions a, .threadType-freebies a, .threadType-vouchers a, .comment-count, .navDrop-list-item-link, #bottom-keywords, .dummy {

  color: rgb(190,190,190) !important;

}

.thread-description, .thread-submission-text-date, .current, .comment-content, .thread-additionalField-content, .thread-additionalField-content .link--hoverUnderline, .thread-tagList-item-link, .paginationButton, #comment-content, .text--muted, p:nth-child(2), .hukd-subLabel, .hukd-text--plain, .thread-posterFeedback-negative , .thread-posterFeedback-neutral, p, .tab-link {

  color: rgb(150,150,150) !important;

}
  
.comment-content:hover, .comment-threadType-deals:hover, .comment-threadType-feedback:hover, .comment-threadType-ask:hover, .comment-threadType-misc:hover, .comment-threadType-for-sale-trade:hover, .comment-threadType-competitions:hover, .comment-threadType-freebies:hover, .comment-threadType-vouchers:hover, .navDrop-list-item-link:hover { 

  background-color: rgb(30,30,30) !important; 

}
  
.thread, .comment-threadType-deals, .comment-threadType-feedback, .comment-threadType-misc, .comment-threadType-for-sale-trade, .comment-threadType-competitions, .comment-threadType-freebies, .comment-threadType-ask, .comment-threadType-vouchers {

  border-style: solid none none none !important;
  border-width: 1px !important;
  border-color: rgb(64,64,64) !important;

}
 
.thread-description--listingPage .link, .link, .sidebarListWidget-list-item-link, .widgetThreadList-item-title-link, .sidebarCarouselWidget-text--link, .hide--toW2, .active, .thread-additionalField-content-readMore, .thread-commentsIndicator {

  color: white !important;

}  
  
#sidebar-top-unanswered-questions .current .link, .voucherReveal-instructions, #addFile, #saveProfileSettingsBtn, #end-date, #start-date, .imgFrame, #image-url {

  color: #1a1a1a !important;  
  background-color: rgb(120,120,120) !important;
  border: 1px solid rgb(35,35,35) !important;

}


.carousel-list-item-image-link, .sidebarCarouselWidget-imageBox, .thread-imageLink {

  border: 1px solid rgb(64,64,64) !important;

}
  
.bbcode_quote_head, .expand-quote { 
  
  color: black !important; 

}
  
.voucherReveal-peel-bottom-code, .thread-merchantLink-link {
  
  color: rgb(0,120,230) !important; 
  
}
  
.modal-content-headline.space--v-3.space--h-4 {

  color: rgb(26,26,26) !important; 
  background-color: rgb(166,166,166) !important;
  border-style: solid solid none solid !important;
  border-width: 1px !important;
  border-color: #404040 !important;

}  
  
.customiseTabPopup-content-sectionHeading {

  color: rgb(217,217,217) !important;  
  background-color: rgb(40,40,40) !important;
  border-style: solid none solid none !important;
  border-width: 1px !important;
  border-color: rgb(0,0,0) !important;

}
  
.customiseTabPopup-content-sectionBody, .customiseTabPopup-footer {
  
  border: 0px solid black !important;
  
}  
  
.customiseTabPopup {
  
  border: 1px solid black !important;
  background-color: rgb(23,23,23) !important;
  
}  
  
.comment-listing-header, .listingHeader {
  
  border-style: none none solid none !important;
  border-width: 1px !important;
  border-color: rgb(100,100,100) !important;

}  
  
.submitForm-optional-heading.hd-4 span {

  background-color: rgb(77,77,77) !important;
  border: 0px solid rgb(10,10,10) !important;

}
  
#sidebar-hottest-deals .current .link {

  color: rgb(217,217,217) !important;  
  background-color: rgb(23,23,23) !important;
  border-style: solid solid none solid !important;
  border-width: 0px !important;
  border-color: rgb(60,60,60) !important;

}
  
.temperature--hot, .hundreds { 
  
  color: #cc0000 !important; 

}

.thread-actions-list, .quote { 
  
  color: rgb(191,191,191) !important;

}
     
.thread-votingControl--up {

  border: 1px solid rgb(35,35,35) !important;

}
  
.hukd-section--borderTop {
  
  border-style: solid none none none !important;
  border-width: 1px !important;
  border-color: rgb(100,100,100) !important;

}   
  
.threadType-deals a { 
  
  border: none !important; 
  
}    
  
.space--fromW4-l-1 {
  
  background-color: rgb(40,40,40) !important;
  border-style: solid none none none !important;
  border-width: 1px !important;
  border-color: rgb(0,0,0) !important;

}
  
.bbcode_quote_body { 
  
  color: #1a1a1a !important; 

}

.subhead-wrapper {

  color: rgb(26,26,26) !important;  
  background-color: rgb(19,19,19) !important;
  border: 0px solid black !important;

} 
 
.bbcode_quote{
  
  background-color: rgb(120,120,120) !important;
  border: 1px solid rgb(15,15,15) !important;

}
  
  
.thread-votingControl--down:hover {
  
  background-color: rgb(0,75,200) !important; 
  
}  
  
.vAlign--all-t {
  
  background-color: rgb(0,75,200) !important; 
  
}    
   
.button--expanded:hover, .button--color-greyBordered:hover, .thread-buttonLink:hover, .paginationButton:hover, .tab-link:hover, .carousel-button--next:hover, .carousel-button--previous:hover {
  
  background-color: rgb(50,50,50) !important;
  
}
  
.carousel-button--inactive, .carousel-button--inactive:hover {
  
  background-color: rgb(30,30,30) !important;
  border-style: solid none none none !important;
  border-width: 0px !important;
  border-color: rgb(0,0,0) !important;

}  
  
.paginationButton--current, .paginationButton--current:hover {
  
  background-color: rgb(70,70,70) !important;
  color: rgb(190,190,190) !important;
  border: 1px solid rgb(0,0,0) !important;

} 

.tab--current .tab-link, .tab--current .tab-link:hover {
  
  background-color: rgb(23,23,23) !important;
  color: rgb(190,190,190) !important;
  border-style: solid solid none solid !important;
  border-width: 1px !important;
  border-color: rgb(0,0,0) !important;

}    
  
#submitButton--password-email {
  
  color: rgb(217,217,217) !important;  
  background-color: rgb(110,136,0) !important;
  border-style: solid solid solid solid !important;
  border-width: 0px !important;
  border-color: rgb(60,60,60) !important;
  
}
  
#submitButton--password-email:hover {
  
  color: rgb(217,217,217) !important;  
  background-color: rgb(110,148,0) !important;
  border-style: solid solid solid solid !important;
  border-width: 0px !important;
  border-color: rgb(60,60,60) !important;
  
}
  
#cancelButton--password-email, #customiseTabPopup_form_cancel {
  
  color: rgb(217,217,217) !important;  
  background-color: rgb(31,31,31) !important;
  border-style: solid solid solid solid !important;
  border-width: 0px !important;
  border-color: rgb(60,60,60) !important;
  
}
  
#cancelButton--password-email:hover, #customiseTabPopup_form_cancel:hover {
  
  color: rgb(217,217,217) !important;  
  background-color: rgb(50,50,50) !important;
  border-style: solid solid solid solid !important;
  border-width: 0px !important;
  border-color: rgb(60,60,60) !important;
  
} 
  
.hukdHeader-row--toW4-expand {

  background-color: rgb(23,23,23) !important;
  border: 1px solid rgb(0,0,0) !important;

}
  
}
