css:
@-moz-document domain("discordapp.com") {
body {
    color:#e0e0e0!important;
}

.btn-group .btn {
    background:#330000!important;
    border-right:#200000!important;
}

.btn-mute, .btn-deafen, .btn-settings {
    box-shadow:none!important;
}

.channel-text .active {
    background:#330000!important;
}

.channel-text:hover {
    background:#600000!important;
}

.channel-text.selected:before {
    border-left:4px solid #600000!important;
}

.channel-text:hover:before {
    border-left:4px solid #600000!important;
}

.guilds, .guilds-wrapper, .help-container, .help-container:after {
    background:black!important;
}

.private-channels, .guild-channels, .guild-header header, .account, .links, .channel-members, .title-wrap {
    background:#111111!important;
    background-color:#111!important;
}

.messages, form, .flex-spacer, .typing {
    background:#181818!important;
}

.comment .markup pre code {
    background:#222!important;
}

#twitchcord-button {
background-image: url(https://cdn.frankerfacez.com/emoticon/72267/1)!important
}

#twitchcord-button.twitchcord-button-open {
background-image:
url(https://cdn.frankerfacez.com/emoticon/35694/1)!important
}

.active .guild-inner:before {
    background:#600000!important;
}

.unread .guild-inner:before {
    background:#400000!important;
}

.header, .channel-name, .active {
    color:#e0e0e0!important;
}

.divider.divider-red span, .divider span {
    background-color:transparent!important;
}

.account, .links {
    border-color:#111!important;
}

.channel-textarea-inner {
    background:131313!important;
    border-color:#444!important;
}

.tooltip {
    background:#330000!important;
    color:#e0e0e0!important;
}

.tooltip.tooltip-right:after {
    border-right-color:#330000!important;
}

.tooltip.tooltip-top:after {
    border-top-color:#330000!important;
}

.chat>.content :first-child .scroller-wrap ::-webkit-scrollbar-thumb {
    background-color:#600000!important;
    border-color:#330000!important;
}

.chat>.content :first-child .scroller-wrap ::-webkit-scrollbar-track-piece {
    background:#2c0000!important;
    border-color:#330000!important;
}

a, a:visited {
    color:#600000!important;
}

.chat .new-messages-bar{
  background-color:#330000!important;
}
.chat .new-messages-bar:hover{
  background-color:#330000!important;
}
.new-messages-bar button{
color:#e0e0e0!important;
}

/*Settings Menu*/
.settings-right .settings-inner, .settings-right .settings-actions {
    background:#111!important;
    border-color:#111!important;
}

.settings-header {
    background:black!important;
}

.modal-inner {
    border:1px solid #600000!important;
}

.tab-bar-item.selected:before, .avatar-uploader-inner {
    border-color:#600000!important;
}

.tab-bar-item:hover:before {
    border-color:#800000!important;
}

.control-group input {
    background-color:#222!important;
    border-color:#111!important;
    color:#e0e0e0!important;
}

.checkbox-inner span {
    background-color:#600000!important;
    border-color:#600000!important;
}

.settings-actions .btn-primary {
    background:#600000!important;
}

.settings-inner .scroller-wrap ::-webkit-scrollbar-thumb {
    background-color:#600000!important;
    border-color:#330000!important;
}

.settings-inner .scroller-wrap ::-webkit-scrollbar-track-piece {
    background:#2c0000!important;
    border-color:#330000!important;
}

.connection-integrations {
    border-color:#111!important;
}

/*User Popout*/
.user-popout-options {
    background:#111!important;
}

.user-popout header {
    background:black!important;
}

.user-popout .btn.btn-mute {
    background:black!important;
}

.btn[data-reactid=".0.4.$=1$14.0.1.3"] {
    background:#600000!important;
    border-color:#600000!important;
}

.user-popout-options .btn.btn-server {
    background:transparent!important;
    border-color:#600000!important;
    color:#600000!important;
}

.user-popout-options .btn {
    background-color:#600000!important;
    border-color:#600000!important;
}

.slider-bar-fill {
    background:#600000!important;
}

.slider-handle {
    background:#330000!important;
    border-color:#330000!important;
}

.slider-bar {
    background:#222!important;
}

.member-role {
    background:#600000!important;
    border-color:#600000!important;
    color:black!important;
}


}
