css:
@-moz-document domain("discordapp.com") {
/* Basic Appearence */
body {
    color:#e0e0e0!important;
}
.app {
    background-image:url("https://i.imgur.com/V1TB28P.jpg") !important;
    background-size:100% 100%;
}

.guilds-wrapper, .channels-wrap, .title-wrap, .content {
    background:rgba(50,50,50,0.4)!important;
}

.guild-channels, .chat, .help-container, .help-container:after, .account, .links, [data-reactid=".0.1.1.0.1.0.0"], .channel-members {
    background: transparent!important;
}

[data-reactid=".0.1.1.0.1.0.0.1"] li a {
    color:rgba(0,0,0,0)!important;
}

.guild-header-open[data-reactid=".0.1.1.0.1.0.0"] li a {
    color:rgba(0,0,0,1)!important;
    background-color:white!important;
}

.guild-header ul {
    box-shadow:none!important;
    background:transparent;
}

.guild-header-open ul {
    background:black!important;
}

.guild-header header {
    background-color:transparent!important;
}

/* Guild Channel Stuff */
.channel .active, .channel-text.selected {
    background:rgba(50,50,50,0.3)!important;
    
}

.channel-text:hover {
    background:rgba(50,50,50,0.9)!important;
}

.channel-text.selected:before {
    border-left:4px solid rgba(50,50,50,0.9)!important;
}

.channel-text:hover:before {
    border-left:4px solid rgba(50,50,50,0.9)!important;
}

.theme-dark .chat form .typing {
    background-color:transparent;
}

/* Twitchcord Button */
#twitchcord-button {
background-image: url(https://i.imgur.com/UsfTI19.png)!important;
    background-color:transparent;
}

#twitchcord-button.twitchcord-button-open {
background-image:url(https://i.imgur.com/UsfTI19.png)!important;
    background-color:transparent;
}

/* Member's List */
.member:hover, .member.popout-open {
    background:rgba(50,50,50,0.6)!important;
}

/* Tooltip */
.tooltip {
    background:rgba(50,50,50,0.6)!important;
    color:#e0e0e0!important;
}

.tooltip.tooltip-right:after {
    border-right-color:rgba(50,50,50,0.6)!important;
}

.tooltip.tooltip-top:after {
    border-top-color:rgba(50,50,50,0.6)!important;
}

/* Scrollbar */
.chat>.content :first-child .scroller-wrap ::-webkit-scrollbar-thumb {
    background-color:rgba(50,50,50,0.9)!important;
    border-color:rgba(30,30,30,0.9)!important;
}

.chat>.content :first-child .scroller-wrap ::-webkit-scrollbar-track-piece {
    background:rgba(0,0,0,0.5)!important;
    border-color:rgba(30,30,30,0.9)!important;
}

/*User Popout*/
.user-popout-options {
    background:rgba(50,50,50,0.75)!important;
}

.user-popout header {
    background:rgba(30,30,30,0.9)!important;
}

.user-popout .btn.btn-mute {
    background:rgba(30,30,30,0.9)!important;
}

.btn[data-reactid=".0.4.$=1$14.0.1.3"] {
    background:rgba(30,30,30,0.9)!important;
    border-color:rgba(30,30,30,0.9)!important;
}

.user-popout-options .btn.btn-server {
    background:transparent!important;
    border-color:rgba(30,30,30,0.9)!important;
    color:rgba(30,30,30,0.9)!important;
}

.user-popout-options .btn {
    background-color:rgba(30,30,30,0.9)!important;
    border-color:rgba(30,30,30,0.9)!important;
}

.slider-bar-fill {
    background:rgba(30,30,30,0.9)!important;
}

.slider-handle {
    background:rgba(50,50,50,0.9)!important;
    border-color:rgba(50,50,50,0.9)!important;
}

.slider-bar {
    background:black!important;
}

.member-role {
    background:rgba(30,30,30,0.9)!important;
    border-color:rgba(30,30,30,0.9)!important;
    color:#e0e0e0!important;
}

/* Chat */
.divider span {
    background:transparent!important;
}

.chat .user-name, .chat .message-text {
    text-shadow: 1px 1px 1px black!important;
}

h2 span {
    color:#e0e0e0!important;
}
}
