css:
@-moz-document domain("c2.com") {
html {
    background: #ccc;
}

#page {
    max-width: 800px;
    font-family: Georgia, serif;
    margin-left: auto;
    margin-right: auto;
    margin-top: 30px;
    margin-bottom: 30px;
    padding: 50px;
    font-size: 13pt;
    box-shadow: 0px 0px 20px 3px #808080;
    border-radius: 4px;
    line-height: 140%;
    background: white;
}

a:visited {
    color: #7e3600;
}

a {
    color: #b95000;
    text-decoration: none;
}

a:hover {
    text-decoration: underline;
}

h1, h2, h3, h4, h5, h6 {
    font-family: "DejaVu Sans", Verdana, sans;
}
}
