css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.vrzone.com") {

body {
background: #111;
}
.toolsmenu, .navlinks {
background: #222;
}
.bbcode_container div.bbcode_quote, .pagination span a, .pagination span a.popupctrl {
background: #4c5460;
}
.postbitlegacy .postrow, .eventbit .eventrow, .postbitdeleted .postrow, .postbitignored .postrow, .postbitlegacy .postbody, .eventbit .eventdetails .eventbody, .forumhead + .childforum .L2:first-child .forumrow, .forumhead + .L2 .forumrow, .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow, .threadbit .sticky, .wgo_block .blockbody{
background: #3c4450;
}
.postbitlegacy .postdetails, .eventbit .eventdetails, .postbitlegacy .userinfo, .postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols, .forumbit_nopost .subforumdescription, .threadbit .nonsticky, .threadbit .discussionrow, .forum_info .blockbody, .blocksubhead {
background: #2c3440;
}


.toolsmenu .popupgroup a.popupctrl, .toolsmenu .popupgroup .popupmenu a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover .popupctrl a.popupctrl.active, .navlinks, .lastedited, .time, .postbit, .postbitlegacy, .eventbit, .postbitlegacy .postfoot .textcontrols a, .postbit .postfoot .textcontrols a, .eventbit .eventfoot .eventcontrols a, .postbitlegacy .postfoot .textcontrols span.mobile, .postbit .postfoot .textcontrols span.mobile, .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow, .forum_info .blockbody, .wgo_block .blocksubhead, .wgo_block .section {
color: #999;
}
.postbitlegacy .postrow, .eventbit .eventrow, .postbitdeleted .postrow, .postbitignored .postrow, .forumbit_post .foruminfo .forumdata .forumtitle a, .forumbit_nopost .forumbit_nopost .forumrow .forumtitle a, .pagination span a.popupctrl {
color: #bbb;
}
.footer a {
color: #4c5460;
}


.toolsmenu, .navlinks, .postbit, .postbitlegacy, .eventbit, .threadbit .sticky, .threadbit .nonsticky, .threadbit .deleted, .threadbit .discussionrow, .threadbit .ignored, .pagination span a, .pagination span a.popupctrl, .wgo_block .blockbody {
border-color: #1c2330;
}
.signature, .postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols, .wgo_block .blocksubhead {
    border-top: 1px solid #1c2330;
}
.forumbit_nopost .forumrowdata, .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow, .forum_info .blockbody, .footer, .blocksubhead {
    border-bottom: 1px solid #1c2330;
}
.postbitlegacy .postbody, .eventbit .eventdetails .eventbody, .forumbit_nopost .forumrowdata, .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow, .forum_info .blockbody {
    border-left: 1px #1c2330;
}
.forumbit_nopost .forumrowdata, .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow, .forum_info .blockbody {
    border-right: 1px solid #1c2330;
}

}
