css:
@-moz-document domain("forumua.org") {
body{
    font: 14px Tahoma, sans-serif;
    color: #000000;
}

.b-media__body,.forum-info,.message-body
{
font: 8px Tahoma, sans-serif;
    color: #000000;
}
.js-post
{
border: 1px solid #989898 ;
    box-shadow: 0 0 5px rgba(0,0,0,0.5);
}
.menu_link
{
/*font: bold 11px Tahoma, sans-serif;*/
    font: italic 14px Segoe UI, sans-serif !important;
}
.b-post__timestamp, .b-post-control__label, .b-post__count,.votes-count,.post-signature
{
font: 10px Tahoma, sans-serif;
    color: #000000;
}

.forum-desc, .subforum-list, .lastpost-info, .posts-count, .topics-count, .category_title, .b-post-sm__post-content,.b-post-sm__author,.topic-wrapper,.notifications-container,.h-clearfix
{
font: 12px Tahoma, sans-serif;
    color: #000000;
}
/*текст в середине сообщения*/
.js-post__content-text,.cke_editable
{
font: 23px Segoe UI, sans-serif;
    color: #000000;
}

.lastcomment-wrapper, .topic-info,.message-header
{
font: 12px Tahoma, sans-serif;
    color: grey;
}
.b-post-sm__title,.post-author a
{
text-decoration: none; /*Подчеркивание у ссылок - нет*/
   color: grey; 
}
.message-body,.message_dialog
{
font: 11px Tahoma, sans-serif;
    color: #000000;
}
.message-header
{
font: 12px Tahoma, sans-serif;
    color: #FFFFFF;
}

.crumb,.category,.header-lastpost,.header-topic /*строка Главная Расширенный поиск Результаты поиска*/
{
font: bold italic 18px Segoe UI, sans-serif;
    color: #006600 !important;
    height: 20px;
}
.js-pagenav
{
font: italic 40px Segoe UI, sans-serif;
    color: #FFFFFF;
 /* height: 35px;*/
}
.main-title
{
font: bold italic 20px Segoe UI, sans-serif !important;
    color: green !important;
}
.b-content-entry,.cke_editable,.cke_editable_themed,.cke_contents_ltr,.cke_show_borders,.js-vbulletin-has-placeholder-events
{
font: 23px Segoe UI, sans-serif;
}
}
