css:
@-moz-document domain("jeuxvideo.com") {
.signature-msg:before {
	content: "Signature Cachée - Passer la souris dessus pour la voir";
	font-weight: bold;
	display: block;
	text-align: center;
	transition-delay: .1s;
	overflow: hidden;
	max-height: 300px;
}
.signature-msg:hover:before {
	transition-delay: .5s;
	max-height: 0;
}
.signature-msg p {
	transition-delay: .1s;
	overflow: hidden;
	max-height: 0;
}
.signature-msg:hover p {
	transition-delay: .5s;
	max-height: 300px;
}
.signature-msg {
	margin-bottom: 0!important;
	padding: 7px 0!important;
	transition-delay: .1s;
}
.signature-msg:hover {
	margin: .9375rem 0!important;
	padding: 0.75rem 0 0 0!important;
	transition-delay: .5s;
}
}
