css:
@-moz-document domain("slack.com") {

ts-message {
    font-family: "Consolas", "Menlo", "Ubuntu Mono", "Courier", monospace !important;
    color: #E8E8E8;
    font-weight: 400 !important;
    font-size: 14px;
}

body,
#footer,
#msgs_div .unread_divider .divider_label {
    background-color:#262626;

}

#client_header, 
#header,
#channel_members_toggle {
    background-color: #4D5250;
}


#active_channel_name .name {
    color: #64666D;
}

ts-message.active:not(.standalone):not(.multi_delete_mode):not(.highlight), ts-message:hover:not(.standalone):not(.multi_delete_mode):not(.highlight) {
    background: rgba(0, 0, 0, 0) !important;
    /* background-color: #333; */
}

ts-message .action_hover_container a
{
    background-color: #333; 

}

a.message_sender.member {
    display: inline-block !important;
    width: 100px !important;
    overflow: hidden;
    vertical-align: middle;
    border-right: solid 1px;
    text-align: left;
    white-space: nowrap
}

.msg_inline_attachment_thumb {
    opacity: 0.3;
    display: none;
}

#primary_file_button,
#message_edit_form #message-input, #messages-input-container #message-input, .inline_message_input_container #message-input {
    background-color: #404040;
    border-color: #333333;
}

#message_edit_form.focus #primary_file_button:not(:hover), #messages-input-container.focus #primary_file_button:not(:hover), .inline_message_input_container.focus #primary_file_button:not(:hover),
#message_edit_form #message-input:focus, #messages-input-container #message-input:focus, .inline_message_input_container #message-input:focus {
    border-color: #4D5250;
}

#message_edit_form #message-input, #messages-input-container #message-input, .inline_message_input_container #message-input {
    font-family: "Consolas", "Menlo", "Ubuntu Mono", "Courier", monospace !important;
    color: #E8E8E8;
    font-weight: 400 !important;
    font-size: 14px;
}

.inline_attachment_wrapper .clear_both.msg_inline_img_holder.msg_inline_holder.msg_inline_holder_rounded {
    display: none;
}

.inline_attachment_wrapper,
.inline_attachment_wrapper a
 {
    font-size: 12px !important; 
}

.inline_attachment .attachment_bar, .special_formatting_quote .quote_bar {
    background-color: #333 !important
}

#client-ui .monkey_scroll_bar {

    background: #333;
}

#client-ui .monkey_scroll_handle_inner {
    border: 3px solid #4D5250;
    background: #333;
}


.day_divider {
    background: rgba(0, 0, 0, 0);
}

.day_divider hr {
    border-color: #333;
}

.day_divider .day_divider_label {
    background-color: #333;
    color: #fff;
    font-family: "Consolas", "Menlo", "Ubuntu Mono", "Courier", monospace !important;
}

#msgs_overlay_div {
    background: #4D5250;
}
}
