css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("uol.com.br") {
  
  * 
  {
    font-family: arial !important;
  }
  
  body
  {
    background: #fafafa !important;
  }
  
  /* ENXUGA PÁGINA PRINCIPAL */
  #topo, .module, .clima, #rotativoTopoBanner, .mod-horizontal.lista-ecommerce, #banner-970x250-Area, .horizontal-header.horizontal-header-type1.border2-p2-top,
  .mod-horizontal.ecommerce-produtos-container, .mod-horizontal.radio-jogos-container, .mod-horizontal.shopping-produtos-uol, #rodape, .mod-horizontal.softnews-container,
  .horizontal-header.border2-top, .mod-horizontal.area-esporte, .horizontal-cotacoes.border5, .modulo.moduloBanner.moduloSemBorda,
  
  /* ENXUGA SUBPÁGINAS */
  .barrauol-bg.full, #banner-stick, .banner-stick, #header, #header-menu, .info-header, .botoes, #conteudo-auxiliar, .modulos-rodape, #veja-mais-texto, .ferramentas,
  .bgcolor5, .termos-conteudo, .article-top_image, .p-author-local,
  
  /* ENXUGA FAMOSOS */
  .bannersticky-top-container, .UHeader__area__container, .title.container, .col-xs-8.col-lg-3.col-md-3.col-sm-3.col-xs-24, .collection-item.collection-module, .collection-index,
  
  /* ENXUGA FOLHA DE SP */
  #folha-topbar-search, #barrauol, .main-header, .intro, .col-lg-4.col-sm-12.col-xs-12, .entry-meta #toolbar-container, .box-more-blogs, .box-more-posts, .share-footer, .col-lg-12.col-sm-12.col-xs-12, 
  .container.menu2 #top, .container.menu2 .span16, .span4.advertising-column, .container.menu2 .span4, .toolbar.rs_preserve.rs_skip, #tags-related-box, .ads-livrariadafolha.article, #comments-header,
  #corner_box, .masthead, .block.no-print, .col.col--s-1-1.col--m-1-1.col--l-1-3, #conteudo .section-title, #conteudo .toolbar, #banner-180x150-area, .container .center.visible-lg,
  .container .livraria, .outbrain-container, .col.col--s-1-1.col--m-1-2, .author,
  
  /* ENXUGA OMELETE */
  .omelete-ad.superbanner-uol, #menu-fluid, .widget.social-share, .medium-24.large-4.columns.right, .medium-8.large-6.columns.conteudo-gutter-top, .js-right-sidebar-static, .js-menu.row.menu-middle,
  .medium-8.large-6.columns, footer, #outbrain_widget_0, .title-wrapper.widget.disqus.title, #omelete_disqus .subtitle,
  
  /* ENXUGA NOTÍCIAS DA TV */
  .xcontainer.menu_altura_new, .wrapper_menu_new, .chapeu.s6, .fr.side, .bloco.s6, .row.mb20, .xrow .row, .xrow .bloco.s0, .xrow .wrapper_comentarios, .patrocinio_canal, .xcontainer .mb20,
  #menu_movel, #menu_movel .xcontainer .xrow .fl,  #menu_movel .xcontainer .xrow .fr,
  
  /* ENXUGA PÚBLICO */
  .site-header, #article-meta-wrapper, .container .secondary, .entry-options.entry-options-above.group,
  
    /* ENXUGA JOVEM PAN */
  .main_header--wrapper, .channel-nav-bar, .top-ad-bar, .large-4.four.hide-for-small.columns, .row box, .small-12.twelve.columns, .wrapper--footer, .section-box.related-news.clearfix,
  .post-detail.clearfix .tags, .content-ads, .section-box.comments-box.hide-for-small .section-title, .credit-social-bar.social-bar.clearfix, .credit-social-bar.credit-bar.clearfix,
  .related-box
  {
    display: none;
    width:  0 !important;
    height: 0 !important;
  }
  
  /* ORGANIZA ELEMENTOS DA PÁGINA PRINCIPAL DO UOL */
  .mod-horizontal .modulo 
  {
    border-bottom: unset !important;
    margin-top: -2px !important;
  }
  
  .mod-horizontal
  {
    margin-top: 30px !important;
    margin-bottom: 50px !important;
  }  
  
  /* CENTRALIZA ELEMENTOS EM PÁGINAS DIVERSAS */
  .row .span8 /* FOLHA DE SP */
  {
    margin-left: 30% !important;
    display: block !important;
    position: absolute !important;
  } 

}
