css:
@-moz-document domain('game.planetarion.com') {
  .container .header {
    background: #162e3c;
    border: 2px solid #000;
    margin: 0 15px !important;
    padding: 3px;
  }
   
  .container .maintext {
    border-left: 2px solid #000;
    border-right: 2px solid #000;
    margin: 0 15px;
  }
  .container .footer {
    border-bottom: 2px solid #000;
    border-left: 2px solid #000;
    border-right: 2px solid #000;
    margin: 0 15px;
  }
  .shadedbackground {
    background-color: rgba(0, 0, 0, 0.1) !important;
  }
  .shadedbackground2 {
    background-color: rgba(0, 0, 0, 0.2) !important;
  }
  .news_combat table {
    background-color: rgba(0, 0, 0, 0.4) !important; 
  }
  .news_combat table tr th, .news_combat table tr td {
    border: 1px solid #000 !important;
  }
  table, th {
    color: #fff;
  }   
  #page input[type="submit"], #page input[type="button"]{
    border-radius: 0px;
    box-shadow: none;
    background:white;
    border:1px solid black;
  }
  .construction_build {
    text-align: center;
    width: 130px;
  } 
.constructions, .research, .scans, .training, .governments {
  border-bottom: 1px solid black;
}
  #menu {
    border-left: 2px solid #000;
    border-right: 2px solid #000;
  }
  #menu_top {
    background: #162e3c;
    border-top: 2px solid #000;
  }
  .menu_grp, #menu_overview, #menu_preferences, #menu_logout {
    background: #162e3c;
  }
  #menu_bottom {
    border-bottom: 2px solid #000;
    background: #162e3c;
    height: 0px;
    width: 146px;
  } 
  #menu_view_link {
   display:none;
  }  
  #menu_time {
    padding-left: 0 !important;
    text-align: center;
    border-top: 2px solid #000;
    border-bottom: 2px solid #000;
    background: #162e3c; 
    width: 161px;
    color: white;
  } 
  #contents {
    margin: 0 165px;
  }
  #contents_header {
    background: #2d5d78;
    border-top: 2px solid #000;
  }
  #contents_information {
    background: #2d5d78;
  }  
  #contents_footer {
    background: #2d5d78;
  } 
  .container {
    background: #2d5d78;
    padding: 10px;
    width: 730px;
  }  
  #tab_links {
    background: #2d5d78;
  }  
  #tnav {
    background: #162e3c;
    border-left: 2px solid #000;
    border-right: 2px solid #000;
    border-top: 2px solid #000;
    width: 1075px;
    margin-left: -4px;
  }  
  body {
    background: #2d5d78;
    color: #fff;
    font-family: Arial,Helvetica,sans-serif;
  } 
  #logo {
    display:none;
  }  
  .pages {
    background: #2d5d78;
    width:1079px;
  } 
  #footer {
    background: #2d5d78;
  }    
  #header {
    background: #162e3c; 
    border-right: 2px solid #000;
    border-top: 2px solid #000;
    border-left: 2px solid #000;
  } 
  #header_planet {
    background: #162e3c; 
  }
  #header_mail {
    background: #162e3c; 
  }    
  #header_news {
    background: #162e3c; 
  } 
  #header_tick {
    background: #162e3c; 
  }
  #header_metal {
    background: #162e3c; 
  }  
  #header_crystal {
    background: #162e3c; 
  }     
  #header_eonium {
    background: #162e3c; 
  }  
  #header_asteroids {
    background: #162e3c; 
  } 
  #header_value {
    background: #162e3c; 
  } 
  #header_xp {
    background: #162e3c; 
  }  
  #header_score {
    background: #162e3c; 
  }    
  #header_rank {
    background: #162e3c; 
  }     
  #planet_summary {
    background: #162e3c; 
    width: 161px;
    border-left: 2px solid #000;
    border-right: 2px solid #000;
  }    
  #planet_summary_construction, #planet_summary_research, #planet_summary_production {
   background: #162e3c; 
  } 
  #planet_summary_footer {
    background: #162e3c;     
    border-bottom: 2px solid #000;
    height:188px;
    width: 144px;
  }
  #left_menu_mid {
    display:none;
  }
  #right_menu_mid {
    display:none;
  }  
  #left_menu_closed {
    display: none;
  }  
  #right_menu_closed {
    display: none;
  } 
}
