/*	Cloaked Navigation - Select								*/
/*	Makes the select websites as plain as possible			*/
/*	Based on work others - such as Ruvio's Cloaked Navigation - 7.09.2008, Version 3.1 - 14.02.2011						*/
/*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*¤*/

@-moz-document domain("gawker.com"), domain("costco.com"), domain("draftexpress.com"), domain("thewirecutter.com"), domain("thesweethome.com"), domain("fantasyfootballcalculator.com"), domain("walterfootball.com"), domain("io9.com"), domain("money.usnews.com"), domain("lifehacker.com"), domain("clarkhoward.com"), domain("fantasypros.com"), domain("amazingribs.com"), domain("fatwallet.com"), domain("slickdeals.net"), domain("consumerist.com"), domain("mlb.com"), domain("rotoworld.com"), domain("camelcamelcamel.com"), domain("artofmanliness.com"), domain("deadspin.com"), domain("kinja.com"), domain("dumblittleman.com"), domain("cheatsheetwarroom.com"), domain("thefantasyfootballguys.com"), domain("espn.go.com"), domain("nfl.com"), domain("advancedfootballanalytics.com"), domain("footballoutsiders.com"), domain("fangraphs.com"), domain("queenscrap.blogspot.com"), domain("grantland.com"), domain("sportsinjurypredictor.com"), domain("forums.footballguys.com"), domain("si.com"), domain("indeed.com"), domain("glassdoor.com"), domain("theskimm.com"), domain("draftwizard.fantasypros.com"), domain("ikeahackers.net"), domain("fantasysports.yahoo.com"), domain("sports.yahoo.com"), domain("kia-forums.com"), domain("bogleheads.org") {


/*---------------------------------------Background---------------------------------------*/

*{
/*Change the background from "#ffffff" to "none" in order to turn flash ads transparent, and back again to turn javascript forms opaque*/
background-color:#ffffff !important;
background-image:none !important;
border-color:#000000 !important;
color:#000000 !important;


/*---------------------------------------Font---------------------------------------*/
font-size:8pt !important;
font-weight:normal !important;	
font-family: Arial, 
Helvetica, sans-serif !important;
text-decoration:none !important;}


/*---------------------------------------Link---------------------------------------*/
/*Change this settings to make the links more discernible*/

a{text-decoration:none !important;color:#003366 !important;}


/*---------------------------------------Images---------------------------------------*/

img, input[type=image]{opacity: 0.05 !important;}

img:hover, input[type=image]:hover{opacity: 0.5 !important;}

img:active, input[type=image]:hover{opacity: 1 !important;}

object, embed, param{opacity: 0.05 !important;}

object:hover, embed:hover, param:hover{opacity: 0.5 !important;}

object:active, embed:active, param:active{opacity: 0.5 !important;}

/*object, embed, param{display:none !important;}*/

/*#capital{display:none !important;}*/ }
