css:
/* 	Textpattern Forum Tablue Rasa userstyle
	by David DeSandro
	nemoorange.com
	Last revised 14 Dec 2008
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.textpattern.com") {

/* STYLISH STARTS NOW */

/* STYLISH RE BASED */


body, input, textarea, select, optgroup, option {
  font-family: Arial, Helvetica, sans-serif !important;
  color: #333 !important;
}

#punwrap {
  width:840px !important;
  padding:10px !important;
  margin:0 auto !important;
}

.tclcon h3, 
.tclcon p, 
.tclcon .thread {
  margin-left: 0px !important;
}

div.icon {
  display:none !important;
}

.pun th,
.pun td,
div.block div.inbox,
div.blockmenu div.inbox {
  padding: 6px 0px !important;
}

.pun h2 {
  padding: 6px 0px !important;
}


/* STYLISH  COLOURS
---------------------------------------------------------------- */

div.box,
div.codebox, 
#adminconsole fieldset th,
.rowodd, .roweven,
#adminconsole td, #adminconsole th,
#brdtitle, 
.pun h2, #brdheader .elsewhere,
#brdmenu,
td.tc2, td.tc3, td.tcmod,
#postpreview,
#viewprofile dd,
div.forminfo,
#adminconsole fieldset td,
div.blockmenu div.box,
#adstats dd,
.pun th,
div.blockpost h2 {
  background-color: transparent !important;
}

/* table header rows */

.pun th {
  border-top:2px solid #FC3 !important;
}


/* border colours
---------------------------------------------------------------- */

/* gecko's default fieldset borders are really nasty so this gives
them a color without interferring with ie's rather nice default */

.pun div > fieldset {
border-color: #aca899;
}

/* link colours
---------------------------------------------------------------- */

/* this is the default for all links */

.pun a:link, .pun a:visited,
.pun h2 a:link, .pun h2 a:visited, 
#brdmenu a:link, #brdmenu a:visited,
li.maintenancelink a:link,
li.maintenancelink a:visited,
div.postmsg a:link, div.postmsg a:visited {
  color: #963 !important;
}
.pun a:hover,
li.maintenancelink a:hover,
div.postmsg a:hover {
  color: #000 !important;
}

#brdmenu a:hover, .pun h2 a:hover {
  color: #000 !important;
  text-decoration: none !important;
}


/* STYLISH OVER TXPISH */

td.tc2, td.tc3, td.tcl, td.tcr {
  border-bottom: none !important;
  border-top: 1px dotted #CCC !important;
  padding: 6px 0 !important;
}

td.tcl h3 {
  font:bold 14px/20px "Helvetica Neue", Arial, sans-serif !important;
}

h2.cat-heading {
  border-top:1px dotted #CCC;
  font:bold 13px Georgia,serif !important;
  padding:8px 0;
}

div.postleft dl {
  padding: 0 0 !important;
}

tr.dividesticky td {
  border-top: 2px solid #FC3 !important;
}

/* STYLISH ALL NEW STUFF */

body {
  border-top:12px solid #FC3;
}

/* header */

#brdtitle, h1 {
  margin: 0 !important;
  padding: 0 !important;
  line-height:1.0em !important;
}

#brdmenu ul {
  border-top:1px solid #AAA;
  padding:8px 0 4px !important;
  font:bold 13px Georgia,serif;
}

#brdwelcome {
  font:italic 11px/18px Georgia, Serif;
  border-top:1px dotted #CCC;
}
#brdwelcome a {
  text-decoration:none;
}

.top-jump {
  border-top:1px dotted #CCC;
}

.linkst {
  padding:0 !important;
}

#brdheader.block,
#brdstats.block {
  margin-bottom: 6px !important;
}

/* categories */

.blocktable thead {
  font:11px  Georgia ;
  text-transform:uppercase;
  letter-spacing:0.1em;
}

.blocktable table {
  margin-bottom: 30px !important;
  border-bottom: 1px dotted #CCC;
}

tr.inew {
  background:#FFC;
}
.inew + .tclcon {
  background:#FFC;
}

.tc2, .tc3, .tcr {
  color: #666;
  font-size: 0.95em ;
}

/* posts and threads */

.blockpost {
  border-top:2px solid #FC3;
}

h2 span {
  text-transform:uppercase;
  font-size:0.92em;
}

.postleft {
  font: 11px/18px Georgia;
  color:#666;
}

.postright {
  margin:0 !important;
  padding:0 !important;
}

.postmsg {
  padding-top: 1.0em !important;
  border-top: 1px dotted #CCC;
}

.postright p, .postright li {
  line-height: 1.6em !important;
} 

.postfootleft, .postfootright {
  text-transform: uppercase !important;
  font-size: 0.87em !important;
}
.postfootleft p, .postfootright p {
  padding: 0 !important;
}


blockquote {
  color:#555;
  border-color: #FDA !important;
  border-width: 3px 1px 1px 3px !important;
  -moz-border-radius: 10px;
  -moz-border-radius-topleft:0px;
  margin-bottom: 1.0em !important;
}

.pun pre {
  border-color: #CCC !important;
  padding: 10px !important;
  font-size: 1.0em !important;
}

.postsignature {
  color:#666;
  font-size:0.9em;
}

div.postsignature hr {
  width: 100% !important;
}

.pun hr {
  color: #CCC !important;
  background-color: #CCC !important;
}

p.page-links {
  clear:both;
  padding: 6px 0 !important;
  width:50% !important;
}
p.page-links a:link,
p.page-links a:visited {
  padding:2px 2px;
  background:#FFD;
  border:0px solid #FED;
  text-decoration:none;
  -moz-border-radius: 2px;
}
p.page-links a:hover {
  border-color: #963;
  background: #963;
  color:#FFF !important;
}

.linksb {
  padding:1.0em 0 0 !important;
  border-top:1px dotted #CCC;
}

.blockform {
  padding:0 10px !important;
  background:#FFD;
  border:1px solid #CCC;
}



}
