css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("theverge.com") {

  
  body {
    background: #222 !important;
    color: #CCC !important;
  }
  
  .l-page {
    background: #222 !important;
  }
  p, p1, p2, p3{
    color: #CCC !important;
  }
  a{
    background: #222 !important;
  }
  blockquote{
    background: #444 !important;
  }
  h2, h, h3, h4 {
    color: #FA4B2A !important;
  }
  h1{
    color: #FA4B2A !important;
  }
  .m-article__share-buttons {
    display: none !important;
  }
  .m-big-stories, .p-entry-nav {
    display: none !important;
  }  
  .m-nav__container, .m-footer{
    display: none !important;
  }
  .m-article__entry {
    padding-top: 0 !important;
    display: block !important;
  }
  .l-container{
   max-width: 95% !important;
  }
  body.yad-iframe{
    display: none !important;
  }
}
