css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("endchan.xyz") {

  body {
    background: #000000 none repeat scroll 0 0;
    color: #fff;
    font-family: arial,helvetica,sans;
    font-size: 100%;
    margin: 0;
}
.innerPost, .markedPost {
    background: #1a1a1a none repeat scroll 0 0;
    border-color: #1a1a1a;
    border-style: none solid solid none;
    border-width: medium 1px 1px medium;
    display: inline-block;
    max-width: 100%;
    overflow: hidden;
    padding-right: 0.25em;
}
    a {
    color: red;
}
    a:hover{
        color: #3366cc;
    }
    nav {
    background-color: #404040;
    border-bottom: none;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.15);
    position: fixed;
    top: 0;
    width: 100%;
}
    nav > a {
        color: #fff;
    }
    
    .linkQuote {
    color: #fff;
    text-decoration: none;
}
    .linkSelf {
    color: #fff;
    text-decoration: none;
}
    .labelSubject {
    color: #211acb;
    font-weight: bold;
}
    .form-post th {
        background-color: #000;
    }
    .form-post input{
        background: #1a1a1a;
        color: #fff;
        border: 0.2px solid gray;
    }
    #postBox textarea{
        background-color: #1a1a1a;
        color: #fff;
        border: 0.2px solid grey;
    }
    
#topBox {
    background-color: #1a1a1a;
    border: none;
    margin: 2.5em auto 1em;
    padding: 0.5em;
    width: 70%;
}
    #newsBox {
    background-color: #1a1a1a;
    border: none;
    padding: 0.5em 0 0.5em 0.5em;
    width: 100%;
}
    .topBoards {
    background-color: #1a1a1a;
    border: none;
    margin: 1em 0 !important;
    padding: 0.5em 0 0.5em 0.5em;
    width: 100% !important;
}
    #divLatestPosts {
    background-color: #1a1a1a;
    border: none;
    margin: 1em 0;
    overflow: hidden;
    padding: 0.5em 0 0.5em 0.5em;
    width: 100%;
}
    #divLatestImages {
    background-color: transparent;
    border: none;
    float: left;
    margin: 0 1em 1.5em auto;
    max-width: 20%;
    padding: 0.5em;
    text-align: center;
}
    footer {
    background-color: #1a1a1a;
    clear: both;
    margin-top: 2em;
    padding: 0.25em 0;
    text-align: center;
    width: 100%;
}
    
#innerWrapper {
    float: right;
    padding-right: 7.8em;
    width: 78%;
}
    #boardsCellHeader span {
    background-color: #1a1a1a;
    border-bottom: 1px solid #000333;
    display: inline-block;
    font-weight: bold;
}
    .boardsCell:nth-of-type(2n+1) span {
    background-color: #1a1a1a;
}
    header #bannerImage {
    width: 223px;
}
    .contentAction, #transferControls, #ipDelete {
    background-color: #1a1a1a;
    border-color: #000;
    border-style: none solid solid none;
    border-width: medium 1px 1px medium;
    clear: both;
    float: left;
    margin: 2em 0 1em 1em;
    padding: 0.5em;
}
    
}
