css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("endchan.xyz") {

/* 
 * Name:    DarkEnd
 * Author:  TheJellyBeanKing
 * Version: 2.3
 * Latest Update: Bug Fixes
 */


/* Background And Font Colour */
body {
    background: #0B0B0B;
    color: #898888;
}


/* Navigation Bar */
nav {
    background-color: #121212;
    border: solid 1px #535353;
    color: #898888;
}


/* Home Page */
h2 {
    float: right;
    margin-right: 2.5em;
}

#topBox, #newsBox, #divBoards, #divLatestPosts, #divLatestImages {
    background-color: #121212;
    border: solid 1px #535353;
    color: #898888;
}

footer {
    background-color: #121212;
    border-top: solid 1px #535353;
    color: #898888;
}


/* Account Page */
.settings {
    background-color: #121212;
    border: solid 1px #535353;
    color: #898888;
}


/* Board Mangment Page */
.settings textarea {
    background-color: #121212;
    border: solid 1px #535353;
    color: #898888;
}


/* Board Page */
#boardsCellHeader span {
    background-color: #121212;
    border: solid 1px #535353;
    color: #898888;
}

.boardsCell:nth-of-type(odd) span {
    background-color: #121212;
    border: solid 1px #535353;
    color: #898888;
}


/* Title */
#labelName {
    font-weight: bold;
}

#labelDescription {
    font-weight: bold;
}


/* Catalog Page */
.catalogCell {
    background-color: #121212;
    border: solid 1px #535353;
    color: #898888;
}


/* Form */
.form-post {
    background: #121212;
    border: solid 1px #535353;
    color: #898888;
    padding: 1em;
}

.form-post th {
    background-color: #121212;
    border: solid 1px #535353;
    color: #898888;
}

#fieldName, #fieldEmail, #fieldSubject, #fieldMessage, #fieldPostingPassword {
    background-color: #121212;
    border: solid 1px #535353;
    color: #898888;
}

#postBox textarea {
    width: 18.5em;
}

#postBox {
	padding-top: 0;	
}

#fieldName, #fieldEmail, #fieldPostingPassword {
    width: 18.8em;
}

input#files {
    width: 18.8em;
}

input#fieldSubject {
    width: 12.9em;
}

/* Post */
.innerPost, .markedPost {
    background-color: #121212;
    border: solid 1px #535353 !important;
    color: #898888;
	font-size: 12px;
    overflow: visible;
}

.linkName {
    color: #117743;
    font-weight: bold;
}

.labelSubject {
    color: #A03F4C;
    font-weight: bold;
}

.linkSelf, .linkQuote {
    color: #898888;
}


/* In Thread */
#modeBanner {
    background-color: #770A0D;
    color: #fff;
    padding-bottom: 0.1em;
    padding-top: 0.1em;
}


/* Quick Reply */
#quick-reply table {
	background-color: #121212;
    border: solid 1px #535353;
}

th {
	background-color: #121212 !important;
    border: solid 1px #535353 !important;
    color: #898888;
}

#quick-reply input, #quick-reply select, #quick-reply textarea {
    margin: 0 0 2px 0;
}

#quick-reply input[type="file"] {
    width: 100%;
    padding: 2px;
    font-size: 10pt;
    box-sizing: border-box;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
}


/* Refresh Area */
.divRefresh {
	display: block !important;
	float: none;
	text-align: center;
	margin-left: auto;
    margin-right: auto;
}

a#toTop {
    text-align: center;
    margin-left: auto;
    margin-right: auto;
    display: block;
}

.threadPages {
	background-color: #121212;
    border: solid 1px #535353;
	color: #898888;
    padding: 0;
    float: none;
	font-size: 12px;
    text-align: center;
    margin-top: 0.5em;
    width: 32%;
    margin-left: auto;
    margin-right: auto;
}

.divPages #divPages, .boardPages #divPages {
	background-color: #121212;
    border: none;
	color: #898888;
}

.threadPages span {
    color: #9393DE;
}

/* Report Box */
.contentAction, #transferControls, #ipDelete {
	align-items: center;
    background-color: #121212;
    border: solid 1px #535353;
	color: #898888;
	/*display: flex;*/
	float: none;
	justify-content: center;
    padding: 0.5em;
	text-align: center;
	margin-right: auto;
	margin-left: auto;
	margin: 0.5;
	margin-top: 0;
	width: 25%;
}

#reportFieldReason {
    margin-left: 1.8em;
}

#deletionFieldPassword {
    margin-left: 0.6em;
}

img#captchaImageReport {
    margin-left: auto;
    margin-right: auto;
	max-width: 100%;
}

a.finePrint {
	display: inherit;
}

.buttonBox {
    display: block;
}

#reportFieldReason, #deletionFieldPassword {
    background-color: #121212;
    border: solid 1px #535353;
    color: #898888;
}

#transferControls, #ipDelete {
    display: none;
}


/* Button And Inputs */
button {
    background-color: #121212;
    border: solid 1px #535353;
    color: #898888;
}

input {
    background-color: #121212;
    border: solid 1px #535353;
    color: #898888;
}

textarea {
	background-color: #121212;
    border: solid 1px #535353;
    color: #898888;
}


/* Scroll Bar */
*::-webkit-scrollbar {
    width: 0.65%;
    height: 10px;
    background: #0B0B0B;
    border: 1px solid #535353;
}

*::-webkit-scrollbar-button {
    display: none;
}

*::-webkit-scrollbar-corner, *::-webkit-scrollbar-track {
    background: #121212;
}

*::-webkit-scrollbar-thumb {
    min-height: 28px;
    background: #828181;
}

*::-webkit-scrollbar-thumb, *::-webkit-scrollbar-track {
    border: 0;
    box-shadow: none;
}

*::-webkit-scrollbar-corner, *::-webkit-scrollbar-track {
    background: #121212;
}

*::-webkit-scrollbar-thumb, *::-webkit-scrollbar-track {
    border: 0;
    box-shadow: none;
}


/* Misc */
::selection {
    background: #010202;
    color: #828181;
}

hr {
    border: 1px solid #535353;
	height: 1px;
}

a {
    color: #9393DE;
}

.quoteLink {
	color: #ff0000;
}

span.spoiler {
    background: #0B0B0B;
    border: 1px solid #535353;
    color: #0B0B0B;
	padding: 1px;
}

span.spoiler:hover {
    background: #0B0B0B;
    border: 1px solid #535353;
    color: #898888;
	padding: 1px;
}

#bannerImage {
    width: 60%;
}

*:focus {
    outline-color: #898888;
}

img.imgFlag {
    display: block;
    margin-left: 5px;
    margin-right: 5px;
}

}
