@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wikipedia.org") {

*{font-family:Calibri,sans-serif!important;}
body{background: #666!important;}

/***********/
/* CLEANUP */
/***********/
#p-logo,#p-personal,#p-tb,#p-Mitmachen,#p-lang,#footer,#siteNotice,#hauptseite-schwesterprojekte,#p-cactions,#n-Contents,#n-featuredcontent,#n-aboutsite,#anon-banner,#p-interaction
{display: none !important;}

/***********/
/* CONTENT */
/***********/



#content{
background:#ddd !important;
max-width:80em !important;
border: 1px #fff solid !important;

}




h1, h2, h3, h4, h5, h6 {
border-color:#666!important;
padding-top:1.2em !important;}




/*ContentBottomNavigation*/
#NavToggle1 {margin-right:5px!important;} /*Fix*/



/***********/
/* SIDEBAR */
/***********/
#column-one{padding-top:36px!important;}
#column-one h5{display: none !important;}

#column-one a {color:#333!important;}
#column-one a:hover{color:#000!important;
text-decoration:none !important;}

.pBody {
background:#ddd!important;
-moz-border-radius-bottomright:0.8em!important;
-moz-border-radius-topright:0.8em!important;}


.pBody ul {
list-style-type:none !important;
list-style-image:none !important;
margin:0.3em 0pt 0pt 0.7em !important;}



}
