css:
@-moz-document url-prefix(https://intra.epitech.eu/) {

  div.header-wrapper {
   background-color: #3c1414 !important;
  }

  div.data {
   background: #CECECE url('http://imgur.com/0MhF7JJ.gif') bottom right no-repeat fixed !important;
   border: 1px solid black !important;
  }
    
  div.item.collapsed:hover, div.content.html:hover, div.title:hover, div.links:hover {
   background: #CDC9C9 !important;
  }

  section, div.infos {
   background-color: #8C1717 !important;
  }

  article {
   background-color: #B22222 !important;
  }
  
  article:hover {
   background-color: white !important;
  }

  div#sidebar, a.infos, div#notification {
   background-color: #521F04 !important;
  }

  div.title {
   color: white
  }
  
  div.content.html {
   color: white
  }
  
  a {
   color: #a8a8a8
   }
  #homeboard section article p.warning {
    color: black
  }
  
  #homeboard section article p.warning a {
    color: black
  }
  
  #homeboard section div.view_more {
    background: #190000
  }
  
   #homeboard section div.view_more {
     color: white
   }
  
  #homeboard section article .timeline .barre span span {
    background: black
  }
  
  #homeboard section article .timeline .barre span {
    background: grey
  }
   
  div.header {
    
  }
  
  #profil #flags .item .header {
    background: white;
    border-color: black;
  }
  
  #profil #flags .item .success {
    background: white;
    border-color: black;
  }
 
   #profil #flags .item .icon.difficulty {
    background-position: -53px -1194px;
    background : transparent url("http://orig04.deviantart.net/61e7/f/2012/093/d/8/cute_cat_emoticon_by_irocandrew-d4ux43m.gif") repeat scroll 0% 0%;
    }
  
  #profil #flags .item .icon.remarkable {
    background-position: -53px -1155px;
    background: transparent url("http://2.bp.blogspot.com/-bEqN4OmYwLU/TeQ34qFlxBI/AAAAAAAAABA/9Tqnah0FNlI/s35/buddy_christ_3.jpg") repeat scroll 0% 0%;
      }
  
  #profil #flags .item .icon.ghost {
    background-position: -53px -1234px;
    background: transparent url("http://www.mariowiki.com/images/thumb/7/76/Boo.gif/35px-Boo.gif") repeat scroll 0% 0%;
  }
 
  #profil #flags .item .icon.medal {
    background-position: -53px -1115px;
    background: transparent url("http://www.citysmiley.com/smiley/banane/banana23.gif") repeat scroll 0% 0%;
  }
 
  #profil #flags .item {
    box-shadow: 4px 4px 4px black
  }
  
  #profil #flags .item .modules {
    background: white  
  }
  
  body#user {
    color: red
  }
  
  #header .intra .navigation tr td .icon {
    margin: 12px auto 0px;
    display: block;
    width: 45px;
    height: 45px;
    background: transparent url("http://image.noelshack.com/fichiers/2016/01/1452390069-sprite-common.png") no-repeat scroll 0% 0%;
    padding: 0px;
  }
  
  #profil .bloc.main .data > .infos {
    color: white
  }

}
