css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.svt.se/svttext/") {


body {background: black !important;background-color: black !important;font-family: arial,helvetica,verdana,sans-serif;font-size: 0.8em;}
body a {background: black !important;}
body div {background-color: black !important;border-right: black !important;border-left:black !important;border-bottom: black !important;}
p {margin: 0 0 .5em 0;}
form {margin: 0;padding: 0;}
#pageinput {background-color: #95caff;position: relative;top: -0.3em;width: 3em;font-size: 0.9em;}
div ul {background: black !important;background-color: black !important;border-right: black !important;border-top: black !important;}  
#submitButton {width: 56px;height: 18px;}
a, a:visited, a:active {color: #0055aa;}
li a, li a:visited, li a:active {color: #00FF00;font-weight: bold;text-decoration: none;}
a:hover, li a:hover, #topNav a:hover, #bottomNav a:hover {text-decoration: underline;color: red;}
div#wrapper {background: black !important;}  
#wrapper {position: relative;border-left: 1px solid black !important;border-right: 1px solid black !important;width: 785px;background-color: black !important;background-repeat: repeat-y;}
.subWrapper {background-color: black !important;margin: 0;clear: both;width: 785px;}
#logo {background: #16002b; width: 787px;margin-bottom: .5em;}
#topLine {border-top: 1px solid black !important;}
ul {background: black !important;background-color: black !important;float: left;margin: 10px 0 0px 0;padding: 0 0 0 0px;list-style-type: none;}
ul#menu {background: black !important;color: #00FF00;}
span.mpNumber {float: right;}
a.mpNumber, a:visited.mpNumber {font-weight: normal;}
li {width: 7em;}
li#help {margin-top: 1em;}
#topNav, #bottomNav {background: black !important;position: relative;width: 665px;height: 2em;color: antiquewhite;background-color: black !important;background-repeat: repeat-x;margin: 0 0 0 auto;}
#topNav a, #bottomNav a {background-color: black !important;color: antiquewhite;text-decoration: none;}
#topNav {background-color: black !important;border-bottom: 1px solid black !important;}
div#topNav {background-color: black !important;}
#linkArea {background-color: black !important;text-align: center;position: relative;width: 665px;height: 2em;background: black !important;margin: 0 0 0 auto;border-bottom: black !important;}
.subArea {background-color: black !important;text-align: center;color: #444;width: 655px;margin: 0 0 0 auto;padding: 5px;display: none;}
.copyArea {background-color: black !important;text-align: center;color: #444;width: 655px;margin: 1em 0 0 auto;padding: 5px;}
#bottomNav, #linkArea {background-color: black !important;border-top: 1px solid black !important;}
.leftSetting {background-color: black !important;position: absolute;left: 0.5em;top: 0.4em;}
.centerLink {background-color: black !important;margin: 0.3em auto 0.3em auto;}
.centerNav {background-color: black !important;width:200px;text-align:center;margin:0em auto 0em auto;padding-top:0.3em;}
.rightSetting {background-color: black !important;position: absolute;right: 0.5em;top: 0.4em;}
.sizeSetting {background-color: black !important;position: absolute;right: 0.5em;top: 0em;}
.aNormal, .aLarger, .aLargest {font-family: monospace;}
.aNormal {font-size: 16px;}
.aLarger {font-size: 19px;}
.aLargest {font-size: 22px;}
.size1 {color: antiquewhite;font-weight: bold;}
.size2 {color: antiquewhite;font-size: 1.2em;}
.size3 {color: antiquewhite;font-size: 1.5em;}
#footerWrapper {background-color: black !important;border-bottom: 1px solid black !important;}
.clear {clear: both;}
.btnBg {background: #FFFF99;}
pre.root {width: 665px;margin: 0 0 0 auto;padding: 1em 0 1em 0;text-align: center;border-bottom: black !important;}
pre.root {color: #00FF00;}
pre.sub {border-top: 1px solid black !important;;}

pre.root { 	font-size: 16px; }
  .root a { color: #0055aa; }
span a:hover {color:red;}  
.BK { color: #00FF00;}
.bgBK { }
.B.bgY.DH {color: #00FF00;}  
.C {color: #00FF00;}
.bgC { }
.G  {color: #00FF00;}
.bgG {background-color: #000 !important }
.M  {color: red;}
.bgM { }
.R  { color: #00FF00;}
.bgR { }
.W  { color: #00FF00;}
.bgW { }
.Y  { color: #00FF00;}
.bgY {color: red }
.B  { color: #00FF00;}
.bgB { }
.DH { font-weight: bold; }
/* .subPageOff { font-weight: bold; }
.subPageOn	{} */
.updateOff	{ font-weight: bold; }
.updateOn	{}
.webView	{ font-weight: bold; }
.tvView		{}
.aNormal 	{ font-weight: bold; }
.aLarger 	{}
.aLargest 	{}
.upArrow	{}
}
