css:
/* ::::: Do not remove this @namespace entry -- it's required for correct functioning ::::: */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


/* Change Thunderbird more Vista like */

#messengerBox treechildren::-moz-tree-row(selected) {
   background-color: #E0EDF3 !important;
   color: black !important;
}

#messengerBox treechildren::-moz-tree-cell-text(selected, focus) {
  background-color: #E0EDF3 !important;
  color: black !important;
}


#folderTree treechildren::-moz-tree-cell-text(folderNameCol, selected) {
  background-color: #E0EDF3 !important;
  color: black !important;
}


menupopup,
popup:not([type="autocomplete"]):not([id="autoscroller"]) {
  background-color: #F7F7F7 !important;
  background-repeat: repeat-y !important;
}

menupopup > menu, 
popup > menu, 
menuitem {
  border-top: 1px solid transparent !important;
  border-right: 1px solid transparent !important;
  border-bottom: 1px solid transparent !important;
  border-left: 1px solid transparent !important;
  margin-top: 0px !important;
}

/* align submenus */
menupopup > menu > menupopup,
popup > menu > menupopup {
  margin-top: -2px !important;
}

menubar > menu {
  border-top: 1px solid transparent !important;
  border-right: 1px solid transparent !important;
  border-bottom: 1px solid transparent !important;
  border-left: 1px solid transparent !important;
  padding-left: 7px !important;
  padding-right: 5px !important;
}

menubar > menu[_moz-menuactive="true"],
menu[_moz-menuactive="true"],
menuitem[_moz-menuactive="true"] {
  border-top: 1px solid #A8D8EB !important;
  border-right: 1px solid #A8D8EB !important;
  border-bottom: 1px solid #A8D8EB !important;
  border-left: 1px solid #A8D8EB !important;
  background-color : #E0EDF3 !important;
  background-image: none !important;
  color: black !important;
}

menubar > menu[_moz-menuactive="true"][open="true"] {
  border-top: 1px solid #666666 !important;
  border-right: 1px solid #666666 !important;
/*  border-bottom: 1px solid #666666 !important; */
  border-bottom: 1px solid transparent !important;
  border-left: 1px solid #666666 !important;
  background-color : #E0EDF3 !important;
  background-image: none !important;
}

menubar > menu[disabled="true"][_moz-menuactive="true"],
menupopup > menu[disabled="true"][_moz-menuactive="true"],
menuitem[disabled="true"][_moz-menuactive="true"],
menubar > menu[disabled="true"],
menupopup > menu[disabled="true"],
menuitem[disabled="true"] {
  border-top: 1px solid transparent !important;
  border-right: 1px solid transparent !important;
  border-bottom: 1px solid transparent !important;
  border-left: 1px solid transparent !important;
  background: transparent !important;
  background-image: none !important;
  color: GrayText !important;
}

*[_moz-menuactive="true"] menuseparator,
menuseparator {
  margin-left: 21px !important;
  margin-right: 2px !important;
}
	
/* make an icon centering adjustment */
.menu-iconic-left {
  margin-left: -1px !important;
}
	
menuitem,
menu {
  margin-left: 0px !important;
  padding-left: 1px !important;
}

/* give the text room to breathe */
menupopup > menuitem > .menu-iconic-text,
menupopup > menuitem > .menu-text,
menupopup > menu > .menu-iconic-text,
menupopup > menu > .menu-text,
popup > menuitem > .menu-iconic-text,
popup > menuitem > .menu-text,
popup > menu > .menu-iconic-text,
popup > menu > .menu-text {
  padding-left: 4px !important;
}

/* Adjust menulists. */
menulist > menupopup > menuitem {
  margin-left: -5px !important;
  padding: 1px 5px 1px 5px !important;
}
.menulist-label {
  margin-left: 2px !important;
}

/* exclusions */
menulist > menupopup,
.menulist-menupopup,
#ubhist-popup {
  background-image: none !important;  
  background-color: #F7F7F7 !important;
}
menulist > menupopup > menuseparator,
.menulist-menupopup > menuseparator,
listcell > menuseparator {
  margin-left: 0px !important;
  margin-right: 0px !important;
}

/* Change Toolbars from Toolbox Design to Native*/
toolbar[type="menubar"]
{
  -moz-appearance: toolbox !important; 
}

#abContactsPanel,
toolbox,
#mail-bar2,
#mail-bar,
#results_box,
sidebarheader,
window statusbarpanel,
#msgHeaderView,
#abDirTreeHeader,
#subscribeWindow
{
  -moz-appearance: none !important;
}
