css:
@-moz-document domain("tgstorytime.com") {
body {
    background-color: #333;
    color: #CCC;
}

a, a:link, a:visited, a:active, .Banner ul li a, ul.PanelInfo li.Active a {
    color: #C6BFFF;
}
a:hover, .Banner ul li a:hover {
    color: #FFF;
}
label, .listbox.even .content, .listbox.odd .content, .listbox.even .content span.label, .listbox.odd .content span.label, .classification, .sublabel, .storyinfo, .listbox .storystats, .notes, span.sbdatetime, .notation, .count, #otherresults, div.Box, ul.DataList div.Meta, ul.MessageList div.Meta, div.PostEdited, #actionmessage, #biocontent span.label {
    color: #999;
}
textarea, textarea:focus, select, input, input.textbox, input.TextBox, input.InputBox, input.textbox:focus, input.TextBox:focus, input.InputBox:focus {
    color: #CCC;
    background-color: #111;
}
textarea:focus, input.textbox:focus, input.TextBox:focus, input.InputBox:focus {
    -moz-box-shadow: 0px 0px 10px #000;
    -webkit-box-shadow: 0px 0px 10px #000;
    box-shadow: 0px 0px 10px #000;
}

.newsbox {
    -moz-box-shadow: 3px 3px 4px #000;
    -webkit-box-shadow: 3px 3px 3px #000;
    box-shadow: 3px 3px 4px #000;
}

#menulist a:link, #menulist a:visited {
    color: #C6BFFF;
    text-shadow: #7A72BB 0 0 3px;
}
#menulist a:hover {
    color: #EEEEEE;
    text-shadow: #C6BFFF 0 0 3px;
}

.bigblock, .block {
    background: #333; /* Old browsers */
	background: -moz-linear-gradient(top, #555 0px, #333 200px); /* FF3.6-15 */
	background: -webkit-linear-gradient(top, #555 0px, #333 200px); /* Chrome10-25,Safari5.1-6 */
	background: linear-gradient(to bottom, #555 0px, #333 200px); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
	box-shadow: 0px 6px 12px #000;
    border: 1px solid #999;
}

.listbox .title, .block .title, .bigblock .sectionheader, #bio #biotitle, #pagetitle, div.HeadingTabs div.SubTab {
    color: #AAA;
}
.listbox .title, .block .title, .bigblock .sectionheader, #bio #biotitle, #pagetitle, ul.DataList a.Title, div.Box h4, div.HeadingTabs div.SubTab {
    text-shadow: #000 0px 1px 0px;
}
#pagetitle a:hover {
    color: #FFF;
}

.listbox .content, .bigblock .content {
    -moz-column-rule: 1px solid #888;
    -webkit-column-rule: 1px solid #888;
}

div.showcase, #shoutlist, ul.PanelInfo li.Active, .Mine {
    background-color: #222;
}

a.linkbutton, a.BigButton, input.button, input.Button, #alphabet a, #pagelinks a, #bio #biotitle a, .Banner ul li a.SignOut {
    box-shadow: 0px 1px 1px #000;
    background-color: #7a72bb;
    color: #FFF;
}
a.linkbutton:hover, a.BigButton:hover, input.button:hover, input.Button:hover, #alphabet a:hover, #pagelinks a:hover, #bio #biotitle a:hover, .Banner ul li a.SignOut:hover {
    background-color: #978ee7;
}
#alphabet #currentletter, #pagelinks #currentpage {
    box-shadow: 0px 1px 1px #000;
}

#tabs span {
    background: #555;
}
#tabs span .count {
    color: #FFF;
}
#tabs #active, #tabs span {
    box-shadow: 0px 2px 4px #000;
    border: 1px solid #555;
}
#tabs #active, #tabs #active a {
    background: #333;
}

.collapsible a, .prev, .next {
    background-color: #555;
    border: 1px solid #333;
    box-shadow: 0px 1px 2px #000;
}
#prev a:link, .collapsible a:link {
    color: #D1CCFF;
}
#next a {
    background-color: #666;
    color: #FFF;
}
#prev a:hover, #next a:hover, .collapsible a:hover {
    background-color: #222;
    border: 1px solid #111;
    color: #FFF;
}

#story {
    background-color: #222;
}

.endbox {
    background-color: #555;
}

div.MessageForm, div.Tabs {
    background: none;
}

div.userpanel {
    background: #444;
}
div.userpanel .count {
    color: #FFF;
}
}
