css:
@-moz-document domain("skyematrixgaming.com") {
body {
    background: rgb(0, 0, 0);
    /* Old browsers */
    
    background: -moz-linear-gradient(left, rgba(0, 0, 0, 1) 0%, rgba(0, 0, 0, 1) 5%, rgba(63, 63, 63, 1) 10%, rgba(29, 29, 29, 1) 15%, rgba(29, 29, 29, 1) 85%, rgba(63, 63, 63, 1) 90%, rgba(0, 0, 0, 1) 95%, rgba(0, 0, 0, 1) 100%);
    /* FF3.6-15 */
    
    background: -webkit-linear-gradient(left, rgba(0, 0, 0, 1) 0%, rgba(0, 0, 0, 1) 5%, rgba(63, 63, 63, 1) 10%, rgba(29, 29, 29, 1) 15%, rgba(29, 29, 29, 1) 85%, rgba(63, 63, 63, 1) 90%, rgba(0, 0, 0, 1) 95%, rgba(0, 0, 0, 1) 100%);
    /* Chrome10-25,Safari5.1-6 */
    
    background: linear-gradient(to right, rgba(0, 0, 0, 1) 0%, rgba(0, 0, 0, 1) 5%, rgba(63, 63, 63, 1) 10%, rgba(29, 29, 29, 1) 15%, rgba(29, 29, 29, 1) 85%, rgba(63, 63, 63, 1) 90%, rgba(0, 0, 0, 1) 95%, rgba(0, 0, 0, 1) 100%);
    /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}
#Bar {
    background: #1d1d1d !important;
    position: fixed !important;
    top: 0 !important;
    z-index: 10 !important;
}
#Footer {
    background: #1d1d1d !important;
    color: #ffffff !important;
}
#Feature_Video {
    margin-top: 5em !important;
    width: 80% !important;
    background: #1d1d1d !important;
    border-radius: 20px !important;
}
img {
    border-radius: 20px !important;
}
#Feature_Video_video {
    background: #1d1d1d !important;
    float: left !important;
    width: 576px !important;
    height: 100% !important;
}
#Feature_Video_title {
    background: #1d1d1d !important;
    border-radius: 20px !important;
    float: left !important;
    width: 30% !important;
    height: 90% !important;
}
#Social {
    width: 80% !important;
    height: 10em !important;
    color: #ffffff !important;
    background: #1d1d1d !important;
    border-radius: 20px !important;
    text-overflow: ellipsis !important;
    white-space: nowrap;
    overflow: hidden;
}
#article_blurb {
    color: #ffffff !important;
    position: absolute !important;
    width: 90% !important;
    white-space: nowrap !important;
    overflow: hidden !important;
    text-overflow: ellipsis !important;
}
#date {
    color: #ffffff !important;
}
#title {
    color: #ffffff !important;
}
#Feature_Video_Title {
    background: #1d1d1d !important;
}
#article_container {
    background: #1d1d1d !important;
}
#header_container {
    background: #1d1d1d !important;
}
#full_article {
    background: #1d1d1d !important;
}
h4 {
    color: #ffffff !important;
}
p {
    color: #ffffff !important;
    white-space: nowrap !important;
    overflow: hidden !important;
    text-overflow: ellipsis !important;
    position: absolute !important;
    width: 90% !important;
}
a {
    color: #ffffff !important;
}
h1 {
    color: #ffffff !important;
}
span {
    color: #ffffff !important;
}
label {
    color: #ffffff !important;
}
}
