css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cnn.com"),
domain("hlntv.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: white !important;
background: black !important;
}

button, .cnn_maincntnr1, #cnn_hdr #cnn_hdr-nav, .cnn_shdcamtt1, .cnn_shdsectbin, .cnn_shdsectbin .cnn_shdcafooter, .cnn_shd755pxfooter, .cnn_shd755px2header, .cnn_maincntnr2, .cnn_shdcaheader, .cnn_shdcafooter, .cnn_shdmnfooter, div.breaking-news, div.cnn_maincntnr5, #show-background, #show-wrapper, .native-facebook-share-count, .pg-right-rail-tall,.pg-rail-tall, nav
{
background: black !important;
}

div, a

{
background-color: black !important;
}

input, span, code, textarea, pre, div

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
