css:
@-moz-document domain("facebook.com") {
body {
    background: url('http://puu.sh/mqBtm/5b320b381a.png') top left no-repeat;
    background-color: #E4E6EA !important;
    background-size: contain;
    background-position: -5% 0px;
    background-attachment: fixed;
}
#sideNav ul .sideNavItem a, #sideNav ul .sideNavItem a:hover, #sideNav ul .sideNavItem a:focus,
.navHeader .rfloat div, .navHeader .rfloat div:hover, .navHeader .rfloat div:focus {
    color: #FFFFFF;
    background: none;
    text-shadow: 1px 1px 1px #3D5B99;
    font-weight: bold;
    text-decoration: none;
}
#sideNav ul .sideNavItem, #sideNav ul .sideNavItem:focus {
	background: none;
}
#sideNav ul .sideNavItem:hover {
	box-shadow: 0px 1px 1px #3D5B99;
    border-radius: 5px;
}
#sideNav .navHeader .sectionDragHandle {
    color: #FFFFFF;
    text-shadow: 0px 0px 5px #000;
}
#sideNav .countValue, #sideNav .countValue:hover, #sideNav .countValue:focus {
    color: #FFFFFF;
}
h1[data-click="bluebar_logo"] a {
    background: url('http://puu.sh/mqBzi/3e9c715501.png') center no-repeat;
    background-size: contain;
}
h1[data-click="bluebar_logo"] span {
    background: none;
}
#blueBarDOMInspector div[role="banner"] {
    background: #FCFCFE url('http://puu.sh/mqHTB/bbb2faf1f1.png') center repeat-x;
    border: none;
}
#blueBarDOMInspector div[role="banner"]:after {
    background: none;
}
#blueBarDOMInspector div[role="navigation"] a {
    text-shadow: 0 0px 5px #000;
}
#blueBarDOMInspector .uiToggleFlyout a {
	text-shadow: none!important;
}
.fbNubFlyoutTitlebar, .fbNubFlyoutTitlebar:focus {
    background: #4E7DC8!important;
    border: solid 1px #4E7DC8!important;
}
.fbNubFlyoutTitlebar:hover {
    background: #3F537A!important;
    border: solid 1px #3F537A!important;
}
.titlebar .titlebarText, .titlebar .titlebarText:hover {
    text-decoration: none;
    font-weight: bold!important;
}
.fbDockChatTabFlyout .fbNubFlyoutBody {
    background-image: url('http://puu.sh/mqBBi/b8afb4e9d1.PNG');
    background-size: cover;
}
.conversationContainer .conversation .direction_ltr, .conversationContainer .conversation .direction_ltr:focus {
    opacity: 0.8;
}
.conversationContainer .conversation .direction_ltr:hover {
    opacity: 0.95;
}
.conversationContainer .conversation .direction_ltr div[data-hover="tooltip"] div div div:hover {
    /*background: #4D7EC8;*/
}
#rightCol .fixed_elem {
    position: initial!important;
}
._li._li._li {
    background: url('http://puu.sh/mqGYN/a695880492.png') top right no-repeat !important;
    background-size: contain!important;
    background-attachment: fixed!important;
    background-position: 90% 0%!important;
}
#contentCol {
    background: none!important;
}
.fbTimelineTimePeriod {
    background: none!important;
}
.fbChatSidebar .fbChatSidebarBody {
    background: url('http://puu.sh/mqEeb/c1bb28879f.png') top center no-repeat;
    background-size: cover;
}
.fbChatOrderedList ul li a:hover {
    background: rgba(0, 0, 0, .1)!important;
}
ul[data-gt*='"ref":"beeper"'] li {
    background: #fff url('http://puu.sh/mqJwR/c1ab85f719.png') center no-repeat;
    background-size: cover;
}
.uiContextualLayer {
	background: none!important;
}
}
