css:
@-moz-document domain("nyaa.se") {
  
  @font-face {
    font-family: "Segoe UI";
    font-weight: 200;
    src: local("Segoe UI Light");
  }
  @font-face {
      font-family: "Segoe UI";
      font-weight: 300;
      src: local("Segoe UI Semilight");
  }
  @font-face {
      font-family: "Segoe UI";
      font-weight: 400;
      src: local("Segoe UI");
  }
  @font-face {
      font-family: "Segoe UI";
      font-weight: 600;
      src: local("Segoe UI Semibold");
  }
  @font-face {
      font-family: "Segoe UI";
      font-weight: 700;
      src: local("Segoe UI Bold");
  }
  @font-face {
      font-family: "Segoe UI";
      font-style: italic;
      font-weight: 400;
      src: local("Segoe UI Italic");
  }
  @font-face {
      font-family: "Segoe UI";
      font-style: italic;
      font-weight: 700;
      src: local("Segoe UI Bold Italic");
  }
  
  * {
    box-sizing: border-box !important;
    font-kerning: normal; 
    font-family: "Segoe UI";
    font-weight: 400;
    font-size: 11px;
    line-height: 25px !important;
    padding: 0;
    margin: 0;
    vertical-align: top !important;
  }  
  a, a:before, a:after {
    color: #222;
    text-decoration: none;
    display: inline-block;
    padding:0;
    margin: 0;
  }
  a:hover {
    text-decoration: dashed;
  }
  b, b:before, b:after {
    display: inline-block;
    font-weight: bold;
  }
  body {
    background: url(http://colourlovers.com.s3.amazonaws.com/images/patterns/3518/3518772.png) repeat 0% 0% #eaeaea !important;
    color: #222 !important;
    margin: 0;
    min-width: 1000px;
    padding: 0;
  }
  form {
    margin: 0;
    padding: 0;
  }  
  input,
  select,
  .inputsearchsubmit {
    display: block;
    float: left;
    font-size: 11px;
    height: 23px;
    margin: 0 2px 0 2px;
    padding: 0 5px 0 5px;
    border: 1px solid #999;
  }
  .inputsearchsubmit {
    line-height: 20px !important;
  }
  textarea {
    resize: vertical;
  }
  label {
    cursor: pointer;
  }
  span {
    display: inline-block !important;
  }
  #main {
    margin-left: auto;
    margin-right: auto;
    padding-bottom: 4px;
    padding-top: 45px;
    width: 1000px;
  }
  #searchcontainer {
    padding: 0;
    margin-top: 11px;    
  }
  #tabnav {
    font-size: 11px;
    height: 40px;
    margin: 0 !important;
  }
  #tabnav a {
    color: #eee;
    padding: 12px 9px 8px 9px;
  }
  #tabnav a:hover {
    text-decoration: dashed;
  }
  #tabnav li {
    display: inline-block;
    list-style-type: none;
    margin: 0;
    padding: 0;
  }
  #topbar {
    background: rgba(25,25,25,0.8);
    background-image: none !important;
    height: 45px;
    position: fixed;
    width: 100%;
  }
  .content { 
    border: none;
    border-radius: 10px;
    margin: 48px auto;
    background-color: #fff;
    font-size: 0.8em;
    padding: 0;
    width: 100%;
    overflow: hidden;
  }
  .content *, .content *:before, .content *:after {
    text-rendering: optimizelegibility !important;
  }
  .aplus {
    background-color: #2ecc71;    
  }
  .hidden {
    background-color: #95a5a6;
  }
  .remake {
    background-color: #FCB336;
  }
  .str {
    display: none;
  }
  .trusted {
    background-color: #3498db;
  }
  
  .aplus .content {
    background-color: #fff !important;
    border: 3px #2ecc71;
  }  
  .hidden .content {
    background-color: #fff !important;
    border: 3px #95a5a6;
  }
  .remake .content {
    background-color: #fff !important;
    border: 3px #FCB336;
  }
  .str {
    display: none;
  }
  .trusted .content {
    background-color: #fff !important;
    border: 3px #3498db;
  }
  
  .aplus *, .hidden *, .remake *, .trusted * {
    color: #222;
  }
  .page {
    border: 1px solid #7f8c8d;
    padding: 4px;
  }
  .pages b {
    padding:4px 6px 4px 6px;
    background-color: #7f8c8d;
    border: 1px solid #7f8c8d;
    color: #222;
    margin: 0!important;
  }
  .pagelink:hover {
    background-color: #ecf0f1;
    border: 1px solid #ecf0f1;
    color: #222;
    text-decoration: none;
  }
  .leftpages {    
    text-align: right;
    width: 10%;
  }
  .pages {
    text-align: center;
    width: 80%;
  }
  .rightpages {
    text-align: left;
    width: 10%;
  }
  .leftpages,
  .pages,
  .rightpages {
    float: left;
    padding: 10px 0 10px;
    margin: 0;
    background-color: #222;    
  }  
  .leftpages *,
  .pages *,
  .rightpages * {
    min-width: 20px !important;
    min-height: 28px !important;
    color: #eee;
  }
  .refinesearch {
    line-height: 2em;
    margin-left: 8px;
    margin-top: 4px;
  }
  .torrentcats {
    display: none;
    height: 0px;
  }
  .torrentsubcatlist {
    padding-left: 18px;
    padding-top: 4px;
  }
  .notice {
    font-weight: bold;
    padding: 0 0 0 15px;
  }
  .eyecatch {
    color: #c0392b;
    font-weight: bold;
  }
  .tlist {
    border: 0;
    border-collapse: collapse;
    margin: 0 !important;    
    table-layout: fixed;
    width: 100%;
  }
  .tlist * {
    word-wrap: unset;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    color: #222;
  }
  .tlistsortorder {
    margin: 0px auto;
  }
  .tlistsortorder * {
    color: #222;
  } 
  .tlisthead {
    height: 38px;
    overflow: hidden;
  }
  .tlistthone {
    width: 10%;
    padding-left: 12px;
    text-align: center;
  }
  .tlistthtwo {
    width: 61%;
  }
  .tlistththree {
    width: 20px;
  }
  .tlistsizehead {
    text-align: center;
    width: 8%;
  }
  .tlistthfive {
    width: 5%;
  }
  .tlistthsix {
    width: 5%;
  }
  .tlistthseven {
    width: 5%;
  }
  .tlisttheight {
    width: 4%;
  }
  .tlistrow {
    border: none;
    height: 40px ;
  }
  
  .tlistrow * {    
    vertical-align: unset;
    vertical-align: middle !important;
  }
  
  .tlistrow:hover {
    background-color: #222;
  }
  
  .tlistrow:hover * {
    color: #eee;
  }
  
  .tlisticon {
    padding: 5px 2px 5px 12px;
  }
  .tlisticon img {
    display: block;
  }
  .tlistname {
    
  }
  .tlistdownload {
    padding-top: 0;
  }
  .tlistsize {
    border:none;
    text-align: center;
  }
  .tlistfailed {
    font-weight: bold;
    text-align: center;
  }
  .tlistsn {
    color: green;
    font-weight: bold;
    padding-left: 4px;
  }
  .tlistln {
    color: red;
    font-weight: bold;
  }
  .tlistdn {
    font-weight: bold;
  }
  .tlistmn {
    color: navy;
    font-weight: bold;
    text-align: center;
  }
  .chatbox {
    display: none;
  }
  .deletebutton {
    background-color: #FBFBFB;
    border: 0;
  }
  .manageheader {
    font-size: 1.2em;
    font-weight: bold;
    text-align: center;
  }
  .manage {
    margin: 0px 20px 0px 20px;
  }
  .container {
    padding: 10px;
  }
  .viewtop {
    border: 0;
    border-collapse: collapse;
    width: 100%;
  }
  .viewtable {
    border: none !important;
    width: 100%;
  }
  .viewcategory {
    font-weight: bold;
    text-align: left;
  }
  .viewflagtorrent {
    font-weight: bold;
    text-align: right;
  }
  .viewtorrentname {
    overflow: hidden;
    word-wrap: break-word;
  }
  .viewmisc {
    float: left;
    width: 70%;
  }
  .viewdownloadbutton {
    float: right;
    margin-top: 2px;
    padding-bottom: 5px;
    padding-top: 10px;
    text-align: right;
    width: 20%;
  }
  .viewdownloadtxtbutton {
    float: right;
    margin-top: 2px;
    padding-bottom: 5px;
    padding-top: 10px;
    text-align: right;
    width: 7%;
  }
  .viewshowhide {
    font-style: italic;
    font-weight: bold;
  }
  .thead {
    font-weight: bold;
    padding-left: 2px;
    text-align: left;
    vertical-align: top;
    width: 10%;
  }
  .viewthtwo {
    width: 60%;
  }
  .viewthfour {
    width: 20%;
  }
  .viewsn {
    color: green;
    font-weight: bold;
  }
  .viewln {
    color: red;
    font-weight: bold;
  }
  .viewdn {
    font-weight: bold;
  }
  .vtop {
    vertical-align: top;
  }
  .viewdescription {
    background-color: #FBFBFB;
    border: 2px solid #666666;
    border-radius: 5px;
    padding: 10px;
    word-wrap: break-word;
  }
  .viewfiletable {
    background-color: #FBFBFB;
    border: 2px solid #666666;
    border-radius: 5px;
    width: 100%;
  }
  .fileentryname {
    border: none !important;
    float: left;
    min-height: 1.3em;
    overflow: hidden;
    padding-left: 10px;
    width: 747px;
    word-wrap: break-word;
  }
  .fileentrysize {
    border: none !important;
    min-height: 1.3em;
    width: 12%;
  }
  .fileentry{
    border: none !important;
  }
  .fileentry * {
    height: 34px !important;
  }
  
  .fileentry:hover {
    background-color: #222; 
    
  }
  
  .fileentry:hover * {
    color: #eee;
  }
  .comment {
    background-color: #FBFBFB;
    border: 2px solid #666666;
    border-radius: 5px;
    margin-top: 4px;
    padding: 4px;
    width: auto;
  }
  .commentoutline {
    background-color: #FBFBFB;
    border: 3px solid #339999;
    border-radius: 5px;
    margin-top: 4px;
    padding: 4px;
    width: auto;
  }
  .chead {
    float: left;
    width: 40%;
  }
  .cnumber {
    float: right;
    font-weight: bold;
    text-align: right;
    width: 3%;
  }
  .cmain {
    clear: both;
    padding: 4px;
    word-wrap: break-word;
  }
  .avatar {
    float: left;
    width: 7%;
  }
  .avatarmoderator {
    float: left;
    width: 37%;
  }
  .avatarnyaa {
    float: left;
    width: 37%;
  }
  .commentform {
    height: 100px;
    width: 600px;
  }
  .commentformdiv {
    text-align: center;
  }
  .spoiler,
  .quote {
    background-color: #FBFBFB;
    border: 2px solid #666666;
    border-radius: 5px;
    padding: 10px;
  }
  .quoteheader {
    font-style: italic;
    font-weight: bold;
  }
  .plink {
    padding-left: 6px;
  }
  .pedit {
    padding-left: 6px;
  }
  .error {
    color: red;
    font-weight: bold;
    padding: 8px;
  }
  .center {
    margin: auto;
    text-align: left;
    width: 50%;
  }
  .flagcontent {
    padding-left: 6px;
    padding-top: 4px;
  }
  .ulform {
    padding: 4px 0px 6px 6px;
  }
  .ulformtable {
    border: 0;
    border-collapse: collapse;
    padding: 0;
  }
  .manageformtable {
    border: 0;
    border-collapse: collapse;
    padding: 0;
  }
  .inputmanagename {
    font-weight: bold;
    vertical-align: top;
    width: 140px;
  }
  .inputmanagetext {
    width: 600px;
  }
  .inputmanagetextarea {
    height: 200px;
    width: 600px;
  }
  .inputmanagesubmitter {
    width: 60px;
  }
  .inputmanagereason {
    width: 150px;
  }
  .inputulname {
    font-weight: bold;
    vertical-align: top;
    width: 125px;
  }
  .inputultext {
    width: 400px;
  }
  .inputulselect {
    width: 404px;
  }
  .inputultextarea {
    width: 100%;
    resize: vertical;
  }
  .inputulrules {
    padding-bottom: 4px;
    width: 400px;
  }
  .inputsearchterm {
    width: 140px;
  }
  .inputsearchcategory {
    width: 140px;
  }
  
  .inputptext {
    width: 500px;
  }
  .inputchatboxmsg {
    width: 600px;
  }
  .inputflagreason {
    height: 150px;
    width: 300px;
  }

  /* Ads hidden */
  
  #main > *:not(.content) {
    display: none;
  }
  
  /* Misc */
  br + br {
    display: none !important;
  }
}
