@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {
  
  body {
    background-color: #252525;
    font-size: 24px;
    }
  
  h1, h2, h3, h4, h5, h6 {
    color: #FFF7F7;
    font-weight: normal;
    text-shadow: 0px 5px 7px #000;
    }
  
  a {
    color: #515151 !important;
    /*background: #202020 none repeat scroll 0% 0%;*/
    }
 
  .mw-wiki-logo {
    background-image: url("http://findicons.com/files/icons/2773/pictonic_free/128/wikipedia.png");
    left: -25px;
    position: relative;
    padding: 6px;
    filter: invert(80%) hue-rotate(180deg);
    }
  
  #left-navigation {
    margin-left: 240px;
    }
  
  
div.vectorTabs {
    float: left;
    height: 2.5em;
    /*background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAuCAIAAABmjeQ9AAAARElEQVR42mVO2wrAUAhy/f8fz+niVMTYQ3hLKkgGgN/IPvgIhUYYV/qogdP75J01V+JwrKZr/5YPcnzN3e6t7l+2K+EFX91B1daOi7sAAAAASUVORK5CYII=");
    background-position: left bottom;
    background-repeat: no-repeat;*/
    padding-left: 0px;
    font-size: 0.8em !important;

}
  
  div.vectorTabs li a {
    display: inline;
    height: 36.1px;
    padding-left: 0.5em;
    padding-right: 0.5em;
    color: #0645AD;
    cursor: pointer;
    /*width: 145.46px;*/
    /*box-shadow: 0px 0px 6px 0px #000;*/
    background: #252525 !important;
    padding-left: 0px;
    font-style: italic;
    font-family: Arial;
    
}
  
  div#mw-head div.vectorMenu h3 {
    float: left;
    /*background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAuCAIAAABmjeQ9AAAARElEQVR42mVO2wrAUAhy/f8fz+niVMTYQ3hLKkgGgN/IPvgIhUYYV/qogdP75J01V+JwrKZr/5YPcnzN3e6t7l+2K+EFX91B1daOi7sAAAAASUVORK5CYII=");
    background-repeat: no-repeat;
    background-position: right bottom;*/
    font-size: 1em;
    height: 2.5em;
    padding-right: 0px;
    margin-right: 0px;
}
  
  div#mw-panel {
    font-size: inherit;
    position: absolute;
    top: 160px;
    padding-top: 1em;
    width: 10em;
    left: 0px;
    }
  
 div#mw-panel div.portal div.body ul li a {
    text-decoration-color: #818181;
    }

  body.page-Main_Page #mp-topbanner, #mp-left, #mp-right, #mv-head, #mp-bottom, #mp-upper, #mp-tfa-h2, #mp-itn-h2, #mp-otd-h2, #mp-dyk-h2, #mp-tfp-h2, .navbox-even, .navbox-list, .navbox-abovebelow, th.navbox-group, .navbox-subgroup .navbox-title, .navbox th, #a, td{
    /*clear: both;*/
    background: #202020 !important;
    color: antiquewhite !important;
    font-size: 18px;
    }

  
 /* div#mw-head {
    position: absolute;
    top: 0px;
    right: 0px;
    width: 100%;
    background-color: #252525;
    font-size: 16px;
    } */

    .MainPageBG {
    background: #353535 !important;
    border: 0px !important;
    color: antiquewhite;
    }
  
  #mw-page-base {
    height: 5em;
    background-position: left bottom;
    background-repeat: repeat-x;
    /*background-image: linear-gradient(#424242 50%, #252525 100%);*/
    background: #252525;
    }
  
  .mw-body .firstHeading {
    overflow: visible;
    color: #D4D4D4;
    }
  
  th {
     color: #FFF;
     background-color: transparent !important;; 
     }
  
  .mw-body {
    margin-left: 10em;
    padding: 1em;
    border-width: 1px 0px 1px 1px;
    border-style: solid;
    border-color: #353535;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    margin-top: -1px;
    background-color: #252525;
    color: #D4D4D4;
    direction: ltr;
    }
  
  #toc, .toc, .mw-warning, .toccolours, .infobox, .catlinks, table.ambox, div.thumbinner, .navbox, body.mediawiki table.mbox-small { 
    border: 1px solid #1A1A1A;
    background-color: #202020;
    padding: 5px;
    font-size: 80%;
    box-shadow: 0px 5px 10px black;
    color: antiquewhite;
    }
  
  .center * {
    text-align: center;
    border-radius: 100% !important;
    }
  
  img {
    border: 5px solid;
    vertical-align: middle;
    background-color: #E7E7E7;
    border-color: #E7E7E7;
    box-shadow: 0px -10px 80px 0px #000;
    }
  
  table.wikitable {
    border-collapse: collapse;
    color: #000;
    }
  
  
  table.wikitable {
    margin: 1em 0px;
    background: #7E7D7D !important;
    background-color: #7E7D7D !important;
    border: 1px solid #212121 !important;
    border-collapse: collapse;
    color: #000;
    }
  
  fieldset {
    border: 1px solid #777;
    margin: 1em 0px;
    padding: 0px 1em 1em;
    }
  
  #pagehistory li.selected {
    background-color: #9E9E9E;
    border: 1px dashed #AAA;
    }
  
  table.tmbox, table.ombox-content {
    margin: 4px 10%;
    border-collapse: collapse;
    border: transparent;
    background: #808080 none repeat scroll 0% 0%;
    }
  
  table.fmbox-editnotice {
    background: #9B7F7F none repeat scroll 0% 0%;
    }
  
    #wpTextbox1 {
    line-height: 1.5em;
    resize: vertical;
    background: rgb(69, 69, 69) none repeat scroll 0% 0%;
    color: orange;
    }
  
  .wikiEditor-ui-toolbar {
    position: relative;
    display: block;
    /*background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAhBAMAAADql9yMAAAAMFBMVEX7/P73+v36/P75+/38/f7t9Prq8vju9fro8vjs9Pnq8/ju9vr7/f70+fv9/v7d3d1xysUWAAAAK0lEQVQIW2N5wMIAhglA3MDiwKIApAXAIiCZA1BZCA8EDwBZDlA1B4CqCwAEyQh1Q/0tlAAAAABJRU5ErkJggg==");
    background-position: left top;
    background-repeat: repeat-x;*/
    background-color: #D5D5D5;
    }
  
  .editOptions {
    background-color: #D5D5D5;
    /*border-width: medium 1px 1px;
    border-style: none solid solid;
    border-color: -moz-use-text-color #170000 #C0C0C0;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;*/
    padding: 1em 1em 1.5em;
    margin-bottom: 2em;
    color: black;
    }
    
    textarea#wpTextbox1 + div#editpage-specialchars, div.wikiEditor-ui-clear + div#editpage-specialchars {
    border-top: medium none;
    background-color: #D5D5D5;
    }
    
    .search-form fieldset {
    background-color: #202020;
    border: 1px solid #202020;
    margin-top: 0.5em;
    padding: 0.7em;
    width: auto;
    box-shadow: 0px 5px 10px #000;
    }
    
}
