@namespace url(http://www.w3.org/1999/xhtml);

/* !!!!!!!!  domain("kinozal.tv") */

@-moz-document domain("kinozal.tv") , domain("kinozal.me")
             , domain("kinozal-tv.appspot.com")
{

     
HTML,
HTML > BODY ,
HTML > BODY div.clr ,
HTML > BODY > div#main ,
HTML > BODY > div#main > div.menu ,
HTML > BODY > div#main > div.content
{
    background: #FFFFF4 !important;
} 
/* футер */
#footer ,
#footerNO  {
   background-image: -moz-linear-gradient(to bottom,
        #FFFFF4, #FDEFCE ) !important;   
}    


     
/*  selection   */
*::-moz-selection  {
   background: #3825CC    !important; 
   background-color: #3825CC    !important; 
   color: #FFFF33   !important; 
   text-decoration:  none !important;  
   text-shadow:  none !important;  
   box-shadow:   none !important;  
   outline: none !important;   
}     
     
/*  !!!!!!!!! ЛИНКИ !!!!!!!!!!!! */  
     
a {
    color: #3063A0   !important; 
}
     
/* обычная раздача */
A[class="r0"] {
    color: #5F5F5F   !important;   /*   5A5A5A */
} 
/*   золото  */
A[class="r1"] {
    color: #E07300   !important; /*  A76416 E0640B FF6600      */
}
/*   серебро  */
A[class="r2"] {
    color: #1279CD  !important;   /* blue 3063A0 */
}
/*   новая раздача  */
A[class="r5"] {
    color: #008500  !important;  
}

/*   visited */
A[class="r0"]:visited ,
A[class="r1"]:visited ,  
A[class="r2"]:visited ,
A[class="r5"]:visited  {   
     color: #661A84    !important; 
}
/*   visited hover */
A[class="r0"]:visited:hover ,
A[class="r1"]:visited:hover ,  
A[class="r2"]:visited:hover,
A[class="r5"]:visited:hover   {   
     color: #9B28C9    !important;  /*  9B28C9 */
}
 
     

A[class="r0"]:hover ,
A[class="r1"]:hover ,  
A[class="r2"]:hover ,
A[class="r5"]:hover      {   
     color: #dd0000    !important; 
}
     
A:hover,
A font:hover,
.label_lf:hover     {
     color: #E14004 !important;   /* #2A8A12 green */
     text-decoration:  none !important;  
}  
A:visited:hover   {
     color: #AC2561 !important;  /*  AC2561 */
}  

     
/* подсветка  строки */
TABLE.t_peer.w100p tr:not([class="mn"]) td:not([class="bt"]) a.r0, 
TABLE.t_peer.w100p tr:not([class="mn"]) td:not([class="bt"]) a.r0:link, 
TABLE.t_peer.w100p tr:not([class="mn"]) td:not([class="bt"]) a.r0:active, 
TABLE.t_peer.w100p tr:not([class="mn"]) td:not([class="bt"]) a.r0:visited, 
TABLE.t_peer.w100p tr:not([class="mn"]) td:not([class="bt"]) a.r1, 
