/* *** Поиск по разделам *** */ 
FIELDSET SELECT[name="forumchoice[]"] OPTION[class="fjdpth0"] {
    background-color: #E8EEFD !important;     
}
FIELDSET SELECT[name="forumchoice[]"] OPTION:hover {
    background-color: #FDEACF  !important;     
}
     
/* строка ввода */ 
