/* кнопка */     
INPUT[type="submit"] ,
INPUT[class*="button"]
{
     height: 20px !important;
     min-height: 20px !important;
     max-height: 20px !important;
}
/* строка ввода */ 
INPUT[type="text"],
INPUT[class="w98p"],
INPUT[type="text"] div {
     font-weight: bold ;
     color: #D1460A !important;     
}
/* вып. список */
SELECT:not([name="forumchoice[]"]) {
     color: #000 ;   
}
/* BORDER */
