INPUT[type="text"] div,
INPUT[class="bginput"] ,
INPUT[type="text"][class="p_srch"] ,
INPUT[type="text"][class="w100"] ,
INPUT[type="text"][class="w90p"] ,
INPUT[type="text"][size="35"] ,
INPUT[type="text"][size="25"] ,
INPUT[type="text"][size="30"] ,
INPUT[type="text"],
