SELECT ,
/* кнопка */     
INPUT[type="submit"] ,
INPUT[class*="button"] ,
BUTTON
{
   border: 1px solid #999999 ;    /*  D6BB8E  ccc  CCC3B8  C1B2A6  rgba(1, 1, 1, .4) */
