INPUT[class="w98p"],
INPUT[type="text"] div, 
INPUT[class="up"] ,
TEXTAREA ,
IFRAME[id*="Editor"]     {
  background-image: -moz-linear-gradient(-84deg, 
       #fff 15%, #FDF6E5 ) !important;  
  /*  FEF9EC FEF7E4 FFF5CD FFFAEF F7F2E7 F7EFD7  */
