  background-size: 100% !important;  
} 
/* вып. список - открытый */
SELECT[class*="styled"],
SELECT[class="w250"]     {
      background-color: rgba(254, 249, 236, .80) ;
}
     
     
/* НЕ вып. список */
SELECT[size="15"][name="t"][class="w100p styled"] {
     height: auto !important;
     min-height:  auto   !important;
     max-height:  inherit   !important;
}    
SELECT[size="15"][name="t"][class="w100p styled"]  >  OPTION:hover {
     background-color: #FDDF9E;
}    
     
     
     
/* кнопка */     
INPUT[type="submit"]:not([class="s_submit"]) ,
INPUT[class*="button"] ,
INPUT[name="upload"] BUTTON  {
   opacity: 1;
   padding-top: 0px !important;
   padding-bottom: 1px !important;  
/* светлый 1   FFF5CD  FDF5E3 FFFDE9 FDE50D FDEA8C      
   background-color: #FFFDE9 !important;       
   background-image: -moz-linear-gradient(to bottom, 
