}     

/* кн. Уменьшить размер /  Увеличить размер */
#vB_Editor_001_cmd_resize_0_100 IMG,  
#vB_Editor_QR_cmd_resize_0_99 IMG,   
#vB_Editor_001_cmd_resize_1_100 IMG,
