/* ****  страница раздачи ******* */  
  
/*  элементы доп. гор. меню */  
/*  Техданные   Релиз    Скриншоты */
/*  Подобные  Топ по жанрам    Топ по персонам    Топ раздающего   */  
.lis a {
     font-size: 98% !important; 
     margin: 0px !important;
     border: none !important;   
     padding: 5px 5px ;  
     text-decoration:  none !important;  
     box-shadow:   none !important;  
}
/* доп. гор. меню - тек.выбор */
.lis LI[class="mn"]  {
}
.lis LI[class="mn"] a:hover {
     cursor:  default   ;
}
#main > DIV:nth-child(2) > DIV:first-child > DIV:nth-child(3) > DIV:nth-child(5) > DIV:first-child > UL > LI a:hover ,
#main > DIV:nth-child(2) > DIV:first-child > DIV:nth-child(3) > DIV:nth-child(6) > DIV:first-child > UL > LI a:hover ,
